insert into international (internationalId,namespace,languageId,message) values (641, 'WebGUI',1,'Image groups are like folders that are used to organize your images. The use of image groups is not required, but on large sites it is definitely useful.\r\n</i></i></p><p><i><i>\r\n\r\n<b>Group Name</b><br>\r\nThe name that will be displayed as you\'re browsing through your images.\r\n</i></i></p><p><i><i>\r\n\r\n<b>Group Description</b><br>\r\nBriefly describe what this image group is used for.\r\n</i></i></p><p><i><i>');
