insert into international (internationalId,namespace,languageId,message) values (73, 'DownloadManager',1,'<b>File Title</b><br>\r\nThe title that will be displayed for this download.\r\n</i></i></p><p><i><i>\r\n\r\n<b>Download File</b><br>\r\nChoose the file from your hard drive that you wish to upload to this download manager.\r\n</i></i></p><p><i><i>\r\n\r\n<b>Alternate Version #1</b><br>\r\nAn alternate version of the Download File. For instance, if the download file was a JPEG, perhaps the alternate version would be a TIFF or a BMP.\r\n</i></i></p><p><i><i>\r\n\r\n<b>Alternate Version #2</b><br>\r\nAn alternate version of the Download File. For instance, if the download file was a JPEG, perhaps the alternate version would be a TIFF or a BMP.\r\n</i></i></p><p><i><i>\r\n\r\n<b>Brief Synopsis</b><br>\r\nA short description of this file. Be sure to include keywords that users may try to search for.\r\n</i></i></p><p><i><i>\r\n\r\n<b>Group To Download</b><br>\r\nChoose the group that may download this file.\r\n</i></i></p><p><i><i>\r\n\r\n<b>Proceed to add download?</b><br>\r\nChoose "Yes" if you have another file to add to this download manager.\r\n</i></i></p><p><i><i>');
