insert into international (internationalId,namespace,languageId,message) values (39, 'Product',1,'WebGUI has a product management system built in to enable you to publish your products and services to your site quickly and easily.\r\n</b></b></i></i></p><p><i><i><b><b>\r\n\r\n<b>Price</b><br>\r\nThe price of this product. You may optionally enter text like "call for pricing" if you wish, or you may leave it blank.\r\n</b></b></i></i></p><p><i><i><b><b>\r\n\r\n<b>Product Number</b><br>\r\nThe product number, SKU, ISBN, or other identifier for this product.\r\n</b></b></i></i></p><p><i><i><b><b>\r\n\r\n<b>Product Image 1</b><br>\r\nAn image of this product.\r\n</b></b></i></i></p><p><i><i><b><b>\r\n\r\n<b>Product Image 2</b><br>\r\nAn image of this product.\r\n</b></b></i></i></p><p><i><i><b><b>\r\n\r\n<b>Product Image 3</b><br>\r\nAn image of this product.\r\n</b></b></i></i></p><p><i><i><b><b>\r\n\r\n<b>Brochure</b><br>\r\nThe brochure for this product.\r\n</b></b></i></i></p><p><i><i><b><b>\r\n\r\n<b>Manual</b><br>\r\nThe product, user, or service manual for this product.\r\n</b></b></i></i></p><p><i><i><b><b>\r\n\r\n<b>Warranty</b><br>\r\nThe warranty for this product.\r\n</b></b></i></i></p><p><i><i><b><b>\r\n');
