insert into international (internationalId,languageId,namespace,message,lastUpdated) values (6,6,'WobjectProxy','Men wobject proxy kan du spegla ett wobject från en annan sida till vilken annan sida som helst. Det är användbart när du vill återanvända innehåll från en sektion av webbplatsen till en annan.\r\n\r\n<b>Wobject till proxy</b>\r\nVälj ett wobject från ditt system som du vill proxya. Väljrutan visar innehåll som "Sidtitel / Wobject namn (Wobject-ID)" så att du snabbt och enkelt kan hitta det wobject du letar efter.', 1032257192);
