insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,6,'UserSubmission','Användarinläggsystem är ett bra sätt att bygga upp en community på webbplatsen och få gratis innehåll från dina användare.\r\n\r\n<b>Design</b>\r\nVad ska inläggsystemet se ut som? Dessa olika vyer finns tillgängliga:\r\n<lu>\r\n <li><b>Traditionell</b> - Skapar en enkel kalkylarksliknande lista av varje inlägg och sorterar efter datum.</li>\r\n <li><b>Webbok</b> - Skapar en vy som liknar nyhetswebbplatsen <a href="http://www.slashdot.org">Slashdot</a>. För att begränsa antalet artiklar på förstasidan kan du använda macrot ^-; där du vill att förstasidans innehåll ska sluta.</li>\r\n <li><b>Fotogalleri</b> - skapar en matris av miniatyrbilder som kan klickas på för att öppna bilden i full storlek.</li>\r\n\r\n<b>Vem kan godkänna?</b>\r\nVälj en grupp som ska godkänna eller refusera inlägg.\r\n\r\n<b>Vem kan bidra?</b>\r\nVälj en grupp som kan bidra med innehåll.\r\n\r\n<b>Inlägg per sida?</b>\r\nHur många inlägg ska visas per sida i inläggsindexet?\r\n\r\n<b>Standardstatus</b>\r\nSka inlägg sättas till <i>godkänd</i>, <i>väntande</i> eller <i>refuserad</i> som standard.\r\n\r\n<i>Note:</i> Om du sätter standardstatus till väntande så måste du själv övervaka meddelandeloggen och godkänna/refusera inlägg.\r\n\r\n<b>Karma per inlägg</b>\r\nHur mycket karma ges till en användare när de bidrar med inlägg?\r\n\r\n<b>Visa miniatyrbilder?</b>\r\nOm det finns en bild med i inlägget kommer denna att visas som en miniatyrbild i designen (se ovan).\r\n\r\n<b>Tillåt diskussion?</b>\r\nVill du koppla ett diskussionsforum till inläggen? Om du gör det så kommer användarna att kunna kommentera inläggen.\r\n\r\n<b>Vem kan skicka?</b>\r\nVälj en grupp som kan skicka kommentarer till inläggen.\r\n\r\n<b>Redigeringstimeout</b>\r\nHur lång tid har en användare på sig att redigera dennes inlägg innan det blir låst? \r\n\r\n<i>Note:</i> Sätt inte denna gränsen för högt. Om användarna tillåts gå tillbaka och ändra långt bak i tiden kan det bli förvirrande när man läser kommentarer till en borttagen text. \r\n\r\n<b>Karma per kommentar</b>\r\nHur mycket karma ges till användare som gör kommentarer till inlägget?\r\n\r\n<b>Vem kan moderera?</b>\r\nVälj en grupp som modererar kommentarerna.\r\n\r\n<b>Modereringstyp</b>\r\nDu kan välja vilken typ av moderering du vill ha på kommentarerna. <i>Efterhand</i> menar att kommentarerna publiceras direkt och tas bort/redigeras i efterhand. <i>Förhand</i> menas att kommentarerna måste godkännas av någon i modereringsgruppen innan dom visas på webbplatsen.\r\n\r\n<b><i>Note:</i><b> oavsett modereringstyp kan modererarna alltid gå in och redigera/ta bort kommentarer och inlägg.', 1031839167);
