insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,6,'Poll','Röstningar kan hjälpa dig göra undersökningar om hur hur dina användare tänker samt vilka synpunkter dom har. \r\n\r\n<b>Aktiv</b>\r\nOm den är kryssad så kan användarna rösta, annars ser de bara resultatet av röstningen.\r\n\r\n<b>Vem kan rösta?</b>\r\nVälj en grupp som kan rösta.\r\n\r\n<b>Karma per röst</b>\r\nHur mycket karma ges till en användare som röstar.\r\n\r\n<b>Bredd på graf</b>\r\nBredden på grafen som visar resultatet. Bredden mäts i pixlar.\r\n\r\n<b>Fråga</b>\r\nVad är frågan du vill ställa?\r\n\r\n<b>Svar</b>\r\nSkriv ner de möjliga svarsalternativen på frågan, ett svar per rad. En röstning kan som mest innehålla 20 svar.\r\n\r\n<b>Slumpa svar</b>\r\nFör att vara säker på att svarens ordning inte påverkar röstarna svara Ja här. Svarens ordning kommer slumpas fram för varje gång sidan visas.\r\n\r\n<b>Nollställ svar</b>\r\nNollställer röstningen.\r\n\r\n<hr>\r\n\r\n<b><i>Note:</i></b> Följande stilmallar används på röstnings wobjektet.\r\n\r\n<b>.pollAnswer</b>\r\nSvaret på en röstning.\r\n\r\n<b>.pollColor</b>\r\nFärgen på procent-mätaren.\r\n\r\n<b>.pollQuestion</b>\r\nFrågan på röstningen.', 1031748292);
