insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,6,'LinkList','<b>Titel</b>\r\nText som kommer bli länkad.\r\n\r\n<b>URL</b>\r\nWebbplats länken leder till. <i>Exempel:</i> http://www.google.com.\r\n\r\n<b>Öppna i nytt fönster</b>\r\nVälj "Ja" om du vill att länken ska öppna ett nytt fönster.\r\n\r\n<b>Beskrivning</b>\r\nKort beskrivning av webbplatsen länken går till. Du kan lämna det här fältet tomt.\r\n\r\n<b>Fortsätt med att lägga till länk?</b>\r\nOm du har fler länkar att lägga till väljer du "Ja".', 1031732803);
