insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,6,'LinkList','Länklistor är precis vad det låter som, en lista med länkar. Många webbplatser har en länksektion och det här wobjektet hjälper dig skapa en sådan.\r\n\r\n<b>Indentering</b>\r\nHur många bokstävers indentering för varje länk?\r\n\r\n<b>Linjeavstånd</b>\r\nHur många radbrytningar mellan varje länk?\r\n\r\n<b>Kula</b>\r\nVälj vilken typ av kula som ska användas i början på varje rad. Du kan lämna det här fältet tomt om du vill. Du kan även välja HTML-kulor som · och ». Du kan även använda bilder från bildhanteraren genom att använda ett macro typ ^I(bullet);.\r\n\r\n<b>Fortsätt med att lägga till länk?</b>\r\nLåt den här stå på "Ja" för att lägga till länkar direkt.\r\n\r\n<b>Stilmall</b>\r\nEn extra stilmallsklass har lagts till för länklistan.\r\n<b>.linkTitle</b> Använd denna för att manipulera utseendet för titeln på varje länk.', 1031732417);
