insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,6,'FAQ','<b>Fråga</b>\r\nSkriv in frågan du vill ha i din FAQ.\r\n\r\n<b>Svar</b>\r\nSkriv in svaret på frågan ovan.\r\n\r\n<b>Fortsätt med att lägga till frågor?</b>\r\nOm du har fler frågor att lägga till väljer du "Ja".', 1031731966);
