insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,6,'FAQ','Det verkar som i princip varenda webplats har en "Frequently Asked Questions"-sektion (ofta ställda frågor). Det här är ett wobject som hjälper dig bygga din egen.\r\n\r\n<b>Sätt på innehållsförteckning?</b>\r\nOm du vill visa en innehållsförteckning för din FAQ. En innehållsförteckning listar länkar till alla frågor i FAQ\'n längst upp på sidan.\r\n\r\n<b>Sätt på Q/A?</b>\r\nVälj om du vill att det ska stå ett Q: framför varje från och ett A: framför varje svar.\r\n\r\n<b>Sätt på [top]-länk?</b>\r\n[top] kommer visas varje fråga och svar. Klickar man på den länken så kommer man längst upp på sidan. Används oftast i samband med en innehållsförteckning.\r\n\r\n<b>Fortsätt med att lägga till frågor?</b>\r\nLåt den vara bockad om du vill lägga till frågor direkt efter du skapat din FAQ.\r\n\r\n<hr>\r\n\r\n<b><i>Note:</i></b> Följande stilar är specifika för FAQ.\r\n\r\n<b>.faqQuestion</b>\r\nEn FAQ-fråga. För att särskilja den från ett svar.', 1031670540);
