insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,6,'EventsCalendar','<b>Titel</b>\r\nTiteln för denna händelse.\r\n\r\n<b>Beskrivning</b>\r\nBeskrivning av aktiviteter för denna händelse eller övrig information om aktiviteten.\r\n\r\n<b>Start datum</b>\r\nVilket datum kommer det börja?\r\n\r\n<b>Slut datum</b>\r\nVilket datum slutar det på?\r\n\r\n<b>Återkommer varje</b>\r\nVälj ett återkomstintervall för denna händelse.\r\n\r\n<b>Fortsätt med att lägga till händelse?</b>\r\nOm du vill fortsätta lägga till händelser låter du "Ja" vara valt.', 1031667712);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,6,'EventsCalendar','Händelsekalendrar används på många intranät för att hålla reda på interna datum som påverkar hela organisationen. Kalendrar på handelsinriktade webbplatser är ett bra sätt att låta kunderna veta bl.a. vilka mässor/utställningar du kommer att medverka på.\r\n\r\n<b>Visningsformat</b>\r\nDetta kan sättas till <i>Lista</i> eller <i>kalender</i>. När du väljer <i>lista</i> kommer händelserna vara listade efter datum (händelser som redan inträffat kommer inte att visas). Den här typen passar bäst när det inte händer mer än ett par saker varje månad. Om du väljer <i>kalender</i> så kommer händelserna visas på en månadskalender som det går att bläddra i. Den här formatet passar sig bäst vid många händelser varje månad.\r\n\r\n<b>Radbryt efter</b>\r\nNär du använder <i>list</i>-formatet så anger du här hur många händelser som kommer visas per sida.\r\n\r\n<b>Fortsätt med att lägga till händelse?</b>\r\nLåt den här vara inställd på "Ja" för att direkt kunna lägga in händelser.\r\n\r\n<hr>\r\n<b><i>Note:</i></b> Följande stil är specifik för händelsekalendern.\r\n\r\n<b>.eventTitle</b>\r\nTiteln för en enskild händelse.', 1031667571);
