insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,6,'DownloadManager','Nerladdningshanteraren är designad för att hjälpa dig hantera distrubitionen av filer på din site. Den hjälper dig välja vilka som ska ha tillträde till filerna.\r\n\r\n<b>Radbryt efter</b>\r\nHur många filer ska visas innan resultatet delas upp i sidor? Med andra ord, hur många filer per sida?\r\n\r\n<b>Visa miniatyrbilder?</b>\r\nKlicka för denna och du vill att användaren ska se miniatyrbilder av bilder du laddat upp. Notera att miniatyrbilden endast syns för huvudfilen. För alternativa versioner visas inga miniatyrbilder.\r\n\r\n<b>Fortsätt med att lägga upp filer?</b>\r\nOm du vill börja lägga upp filer direkt så låt denna vara kryssad.', 1031664796);
