insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,6,'FAQ','Det verkar som i princip varenda webbplats har en "Frequently Asked Questions"-sektion (ofta ställda frågor). Det här är ett wobject som hjälper dig bygga din egen.<p><b>Sätt på innehållsförteckning?</b><br>Om du vill visa en innehållsförteckning för din FAQ. En innehållsförteckning listar länkar till alla frågor i FAQ\'n längst upp på sidan.<p><b>Sätt på Q/A?</b><br>Välj om du vill att det ska stå ett Q: framför varje från och ett A: framför varje svar.<p><b>Sätt på [top]-länk?</b><br>[top] kommer visas varje fråga och svar. Klickar man på den länken så kommer man längst upp på sidan. Används oftast i samband med en innehållsförteckning.<p><b>Fortsätt med att lägga till frågor?</b><br>Låt den vara bockad om du vill lägga till frågor direkt efter du skapat din FAQ.<p><hr><p><b><i>Note:</i></b> Följande stilar är specifika för FAQ.<p><b>.faqQuestion</b><br>En FAQ-fråga. För att särskilja den från ett svar.', 1032859954);
