INSERT INTO international VALUES (611,'WebGUI',21,'<b>Название компании</b><br>\r\nВведите название вашей комнании. Это имя будет использоваться макросом ^c;.\r\n<br><br>\r\n<b>e-mail</b><br>\r\nВведите основной электронный адрес компании. Он быдет подставляться при автоматической генерации сообщений для пользователей сайта.\r\n<br><br>\r\n<b>URL сайта компании</b><br>\r\nВведите основной адрес веб-сайта компании. Этот адрес будет использоваться при автоматической генерации сообщений для пользователей сайта.\r\n<br><br>',1036493480);
