INSERT INTO international VALUES (73,'DownloadManager',21,'<b>Название файла</b><br>\r\nВведите название файла.\r\n<p>\r\n\r\n<b>Выберите файл</b><br>\r\nВыберите файл на своем компьютере при помощи кнопки \"Обзор\".\r\n<p>\r\n\r\n<b>Альтернативная версия файла &#8470;1</b><br>\r\nЗдесь надо указать этот же документ, но имеющий другой формат. Если у документа нет альтернативных версий, то оставьте это поле пустым.\r\n<p>\r\n\r\n<b>Альтернативная версия файла &#8470;2</b><br>\r\nСм. \"альтернативная версия файла &#8470;1\".\r\n<p>\r\n\r\n<b>Краткое описание</b><br>\r\nВведите краткое описание файла.\r\n<p>\r\n\r\n<b>Группа</b><br>\r\nВыберите группу, которая имеет право добавлять файлы.\r\n<p>\r\n\r\n<b>Добавить еще файл?</b><br>\r\nВыберите \"Да\", если вы хотите добавить новый файл в файловый менеджер.\r\n<p>\r\n',1034773253);
