INSERT INTO international VALUES (73,'LinkList',21,'<b>Название</b><br>\r\nВведите текст ссылки.\r\n<p>\r\n\r\n<b>URL</b><br>\r\nВведите URL сайта, на который вы хотите поставить ссылку.\r\n<p>\r\n\r\n<b>Открывать ссылку в новом окне?</b><br>\r\nВыберите \"Да\", если вы хотите, чтобы сайт, на который стоит ссылка, открывался в новом окне.\r\n<p>\r\n\r\n<b>Описание</b><br>\r\nВы можите ввести краткое описание сайта, на который ведет ссылка. Это поле также можно оставить пустым.\r\n<p>\r\n\r\n<b>Добавить еще одну ссылку?</b><br>\r\nЕсли вы хотите добавить новую ссылку, выберите \"Да\".\r\n<p>\r\n',1034073553);
