INSERT INTO international VALUES (73,'EventsCalendar',21,'<b>Название</b><br>\r\nВведите название события.\r\n<p>\r\n\r\n<b>Описание</b><br>\r\nВведите описание предстоящего события.\r\n<p>\r\n\r\n<b>Дата начала</b><br>\r\nВведите дату начала события.\r\n<p>\r\n\r\n<b>Дата окончания</b><br>\r\nВведите дату окончания события.\r\n<p>\r\n\r\n<b>Повторять каждые</b><br>\r\nВыберите промежеток времени, в течении которого повторяется событие. \r\n\r\n<p>\r\n\r\n<b>Добавить еще одно событие?</b><br>\r\nЕсли вы хотите добавить новое событие, выберите \"Да\".\r\n<p>\r\n',1034072497);
INSERT INTO international VALUES (641,'WebGUI',21,'Группы изображений выполнят теже функции, что и папки, т.е. позволяют объединить изображения в группы, связанные по смыслу. Их использование не обязательно, но сильно сокращается время, затрачиваемое на поиск нужного изображения.\r\n<p>\r\n\r\n<b>Имя группы</b><br>\r\nзадайте имя для создаваемой группы.\r\n<p>\r\n\r\n<b>Описание группы</b><br>\r\nВведите краткое описание группы.\r\n<p>\r\n',1034072454);
