INSERT INTO international VALUES (698,'WebGUI',21,'Используя систему наблюдения за действиями пользователей, вы можите контролировать активнось пользователей. Активность пользователей можно поощрать, а можно, наоборот, наказывать пользователей за излишнюю активность.',1036070406);
INSERT INTO international VALUES (709,'WebGUI',21,'<b>Менеджеры графических файлов</b><br>\r\nВыберете группу, имеющую право добавлять, редактировать и удалять графические файлы.\r\n<br>\r\n<b>Менеджеры графический оболочки</b><br>\r\nВыберите группу пользователей, имеющих право на изменение графической оболочки сайта.\r\n<br>\r\n<b>Менеджеры шаблонов</b><br>\r\nВыберите группу пользователей, которая имеет право на добавление, редактирование и удаление шаблонов. \r\n<br>',1036070741);
