INSERT INTO international VALUES (71,'Item',21,'Ссылка на документ предназначена для добавления небольших кусков статической информации.\r\n<br><br>\r\n\r\n<b>URL</b><br>\r\nЭтот URL будет добавлен к заголовку параграфа.\r\n<br><br>\r\n<i>Пример:</i> http://www.google.com\r\n<br><br>\r\n\r\n<b>Вложенный файл</b><br>\r\nЕсли вы хотите добавить документ MIcroSoft Word, архив или любой другой другой тип файла, просто найдите его при помощи кнопки \"Обзор\" на вашем компьютере.\r\n\r\n',1034230847);
