insert into international (internationalId,languageId,namespace,message,lastUpdated) values (4,3,'Survey','Enquête\'s laten u informatie van uw gebruikers verzamelen. In het geval van\r\nWebGUI enquête kunt u ze ook gebruiken om de kennis van uw gebruikers te\r\ntesten.<br>\r\n<br>\r\n<b>Vraag volgorde<br>\r\n</b>De volgorde van de vragen zal gevraagd worden. Sequentieel laat de vragen\r\nzien in de volgorde waarin u ze gemaakt heeft. Willekeurige volgorde laat\r\nze in willekeurige volgorde zien. Antwoord gestuurd laat de vragen zien op\r\nbasis van de antwoorden van de gebruikers.<br>\r\n<br>\r\n<b>Mode</b><b><br>\r\n</b>Standaard start de enquête in enquête mode. Dit geeft u de mogelijkheid\r\nom uw gebruikers vragen te stellen. Als u naar quiz mode wisselt kunt u zelf-verbeterende\r\nantwoorden test van uw gebruikers kennis laten zien.<br>\r\n<br>\r\n<b>Wie kan de enquête doen?</b><br>\r\nWelke gebruikers mogen in de enquête deelnemen?<br>\r\n<br>\r\n<b>Wie kan rapporten bekijken?</b><br>\r\nWie kan de rapporten van de enquête bekijken?<br>\r\n<br>\r\n<b>Hoe verder?</b><br>\r\nAls u dit default laat zal er meteen om een vraag gevraagd worden na het\r\ntoevoegen van de enquête.<br>\r\n', 1039784249);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,3,'SiteMap','Een sitemap wordt gebruikt om extra navigatie te bieden in WebGUI. \r\nEen traditioneel overzicht kan gemaakt worden dat een hierarchisch beeld geeft\r\nvan alle paginas in de webpagina. Ook kunnen pagian overzichten gebruikt worden\r\nom extra navigatie te bieden in een bepaalde niveauvan de pagina.<br>\r\n <br>\r\n <b>Sjabloon</b><br>\r\nKies een layout voor dit paginaoverzicht.<br>\r\n <br>\r\n <b>Vanaf dit niveau beginnen?</b><br>\r\n Als het pagina overzicht de pagina boom weergeeft vanaf het huidige niveau, \r\nvink dan dit vak aan. Vink het uit als de hele boom weergegeven moet worden.<br>\r\n <br>\r\n <b>Aantal niveaus weer te geven</b><br>\r\n Hoeveel niveaus moet het pagina overzicht laten zien. Wanneer 0 is ingevoerd \r\nzullen alle onderliggende niveaus getoond worden.<br>\r\n', 1039784142);
