insert into international (internationalId,languageId,namespace,message,lastUpdated) values (640,3,'WebGUI','Sjablonen verwijderen zonder te weten wat voor bijwerkingen dit kan hebben is niet verstandig (sommige paginas kunnen het sjabloon nog gebruiken). Wanneer ervoor gekozen wordt een sjabloon te verwijderen, zulleen alle paginas die dit sjabloon gebruiken het standaard sjabloon gaan gebruiken.', 1039782887);
