insert into international (internationalId,languageId,namespace,message,lastUpdated) values (618,3,'WebGUI','<b>Herstel Wachtwoord Bericht</b><br>\r\n Het bericht dat een gebruiker toegestuurd wordt wanneer ze de \"herstel wachtwoord\"\r\nfunctie gebruiken.<br>\r\n  <br>\r\n<b>E-mail voettekst</b><br>\r\nDeze voettekst zal gebruikt worden voor macro\'s en e-mail die vanaf WebGUI\r\nverstuurd worden.<br>\r\n<br>\r\n <b>SMTP Server</b><br>\r\n Dit is het adres van de locale mail server. Dit is nodig voor alle kenmerken \r\ndie het Internet email systeem gebruiken (zoals wachtwoord herstellen).<br>\r\n <br>\r\n Als er een sendmail server en het WebGUI systeem op dezelfde machine gedraaid \r\nworden &nbsp;kan er optioneel ook een pad gespecificeerd worden naar het sendmail\r\nprogramma. Dit kan op de meeste Linux systemen gevonden worden in \"/usr/lib/sendmail\".<br>\r\n <br>\r\n', 1039781234);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (611,3,'WebGUI','<b>Bedrijfsnaam</b><br>\r\nDe naam van het bedrijf. Deze zal in alle e-mails en overal waar het bedrijfsnaam\r\nmacro wordt gebruikt verschijnen.<br>\r\n<br>\r\n<b>Bedrijfs E-mail Adres</b><br>\r\nEen algemeen e-mail adres van het bedrijf. Dit is het adres waar alle geautomatiseerde\r\ne-mails van het bedrijf vandaan komen. Het kan ook gebruikt worden met behulp\r\nvan het WebGUI macro systeem.<br>\r\n<br>\r\n<b>Bedrijfs URL</b><br>\r\nHet primaire URL van het bedrijf. Dit zal in alle e-mails, die met het WebGUI\r\nsysteem verstuurd worden verschijnen. Het kan ook gebruikt worden met behulp\r\nvan het WebGUI macro systeem.<br>\r\n', 1039781103);
