insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,3,'EventsCalendar','<b>Titel</b><br>\r\n De titel voor deze gebeurtenis. \r\n<p>  <b>Beschrijving</b><br>\r\n Omschrijf de activiteit van deze gebeurtenis of voer informatie in over\r\nwaar deze gebeurtenis plaatsvind. </p>\r\n<p>  <b>Begindatum</b><br>\r\n Wanneer begint deze gebeurtenis? </p>\r\n<p>  <b>Einddatum</b><br>\r\n Op welke datum zal deze gebeurtenis plaatsvinden? </p>\r\n<p>  <b>Gebeurt iedere</b><br>\r\n Selecteer een interval waarin deze gebeurtenis opnieuw plaatsvind. </p>\r\n<p>  <b>Wat nu?</b><br>\r\n Als u nog een gebeurtenis toe wilt voegen, selecteer dan \"voeg gebeurtenis toe\" selecteer ander \"ga terug naar de huidige pagina\". </p>\r\n<p></p>\r\n<br>\r\n', 1039741404);
