insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,3,'FAQ','<b>Vraag</b><br>\r\n Voer de vraag in die u in de FAQ wilt laten verschijnen. \r\n<p>   <b>Antwoord</b><br>\r\n Voer het antwoord in voor de vraag die u hierboven heeft ingevuld. </p>\r\n<p>   <b>Wat nu?</b><br>\r\n Als u nog een vraag wilt toevoegen, laat dan de default geselecteerd staan. </p>\r\n<p> </p>\r\n<br>\r\n', 1039741313);
