insert into international (internationalId,languageId,namespace,message,lastUpdated) values (832,3,'WebGUI','<p>Het collaterale inhoudsbeheer systeem heeft de beschikking over enkele specifieke macro\'s.</p>\r\n<p><b>^I();</b><br>\r\nDit macro haalt een plaatje uit het collaterale inhoudsbeheer systeem en voegt daaraan een HTML plaatjes tag toe zodat het mogelijk is snel een plaatje uit het collaterale systeem in uw inhoud te plaatsen.</p>\r\n<p><i>Voorbeeld:</i> ^I(\"logo\");\r\n</p>\r\n<b>^i();</b><br/>\r\nDit macro haalt de URL voor willekeurige inhoud uit het systeem.\r\n<p/>\r\n<i>Voorbeeld:</i> ^i(\"status rapportage\");\r\n<p/>\r\n\r\n<b>^Thumbnail();</b><br/>\r\nDit makro geeft de URL voor het pictogram van een plaatje uit het systeem.\r\n<p/>\r\n<i>Voorbeeld:</i> ^Thumbnail(\"trouwfoto\");\r\n<p/>\r\n\r\n<b>^ThumbnailLinker();</b><br/>\r\nDit macro bouwt een plaatje zoals het Thumbnail macro en maakt daar een link van naar het plaatje op volledige groote.\r\n<p/>\r\n<i>Voorbeeld:</i> ^ThumbnailLinker(\"trouwfoto\");\r\n<p/>\r\n\r\n<b>^File();</b><br/>\r\nDit macro maakt een snelle bestandslink. Het maakt een pictogram voor het bestand en laat de bestandsnaam zien. Daarna linkt het naar dit bestand zodat het gedownload kan worden.<p/>', 1039738670);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (77,3,'USS','<P>De volgende sjabloon variabelen zijn beschikbaar om een bijdrage\r\naan te passen. Bijdrage sjablonen worden gebruikt voor het tonen van\r\nindividuele bijdragen in een gebruikers bijdrage systeem. \r\n</P>\r\n<P><B>title</B><BR>De titel van deze bijdrage.</P>\r\n<P><B>content</B><BR>De volledige inhoud van deze bijdrage.</P>\r\n<P><B>user.label</B><BR>Het vertaalde label voor wat deze gebruiker\r\naan het systeem bijgedragen heeft.</P>\r\n<P><B>user.profile</B><BR>De URL naar het profiel van de gebruiker\r\ndie deze bijdrage gepost heeft.</P>\r\n<P><B>user.username</B><BR>De gebruikersnaam van de gebruiker die\r\ndeze bijdrage gepost heeft.</P>\r\n<P><B>user.id</B><BR>Het id waaraan de gebruiker die deze bijdrage\r\ngeleverd heeft geidentificeerd kan worden.</P>\r\n<P><B>date.label</B><BR>Het vertaalde label voor op welke datum deze\r\nbijdrage gepost is.</P>\r\n<P><B>date.epoch</B><BR>De tijd die verstreken is in seconden sinds 1\r\njanuari 1970 waarop deze bijdrage geleverd is.</P>\r\n<P><B>date.human</B><BR>Een voor mensen leesbare datum die de datum\r\nen tijd aangeeft waarop deze bijdrage geleverd is.</P>\r\n<P><B>status.label</B><BR>Het vertaalde label dat de status van deze\r\nbijdrage aangeeft.</P>\r\n<P><B>status.status</B><BR>De status van deze bijdrage\r\n(goedgekeurd,wachtend,afgekeurd).</P>\r\n<P><B>views.label</B><BR>Het vertaalde label voor het aantal keer dat\r\ndeze bijdrage bekeken is.</P>\r\n<P><B>views.count</B><BR>Het aantal keer dat deze bijdrage bekeken\r\nis.</P>\r\n<P><B>canPost</B><BR>Een conditie die aangeeft of deze gebruiker al\r\ndan niet een nieuwe bijdrage mag leveren.</P>\r\n<P><B>post.url</B><BR>De URL om een nieuwe bijdrage te posten.</P>\r\n<P><B>post.label</B><BR>Het vertaalde label voor de post link.</P>\r\n<P><B>previous.more</B><BR>Een conditie die aangeeft of er enige\r\nvoorgaande bijdragen beschikbaar zijn voor deze bijdrage. \r\n</P>\r\n<P><B>previous.url</B><BR>Een URL naar de bijdrage die hiervoor kwam.</P>\r\n<P><B>previous.label</B><BR>Het vertaalde label voor de vorige link.</P>\r\n<P><B>next.more</B><BR>Een conditie die aangeeft of er nog bijdragen\r\nna deze beschikbaar zijn.</P>\r\n<P><B>next.url</B><BR>De URL naar de bijdrage na deze.</P>\r\n<P><B>next.label</B><BR>Het vertaalde label voor de bijdrage na deze.</P>\r\n<P><B>canEdit</B><BR>Een conditie die aangeeft of de huidige\r\ngebruiker deze bijdrage mag bewerken of verwijderen.</P>\r\n<P><B>edit.url</B><BR>De URL om deze bijdrage te bewerken.</P>\r\n<P><B>edit.label</B><BR>Het vertaalde label om deze bijdrage te\r\nbewerken.</P>\r\n<P><B>delete.url</B><BR>De URL om deze bijdrage te verwijderen.</P>\r\n<P><B>delete.label</B><BR>Het vertaalde label voor de link om deze\r\nbijdrage te verwijderen.</P>\r\n<P><B>canChangeStatus</B><BR>Een conditie die aangeeft of de huidige\r\ngebruiker de privileges heeft om de status van deze bijdrage aan te\r\npassen.</P>\r\n<P><B>approve.url</B><BR>De URL om deze bijdrage goed te keuren.</P>\r\n<P><B>approve.label</B><BR>Het vertaalde label voor de link om goed\r\nte keuren.</P>\r\n<P><B>deny.url</B><BR>De URL om deze bijdrage af te keuren.</P>\r\n<P><B>deny.label</B><BR>Het vertaalde label voor de link om af te\r\nkeuren.</P>\r\n<P><B>leave.url</B><BR>De URL om deze post in z\'n huidige status te\r\nlaten.</P>\r\n<P><B>leave.label</B><BR>Het vertaalde label voor de link om de\r\nhuidige status te handhaven.</P>\r\n<P><B>canReply</B><BR>Een conditie die aangeeft of de huidige\r\ngebruiker op deze bijdrage mag reageren.</P>\r\n<P><B>reply.url</B><BR>De URL om op deze post te reageren.</P>\r\n<P><B>reply.label</B><BR>Het vertaalde label voor de reageren link.</P>\r\n<P><B>search.url</B><BR>De URL om het WebGUI krachtzoek formulier aan\r\nof uit te zetten.</P>\r\n<P><B>search.label</B><BR>Het vertaalde label voor de krachtzoek\r\nlink.</P>\r\n<P><B>back.url</B><BR>De URL om de gebruiker terug te laten keren\r\nnaar de hoofdlijst met bijdragen.</P>\r\n<P><B>back.label</B><BR>Het vertaalde label voor het terugkeren.</P>\r\n<P><B>replies</B><BR>Een volledige lijst met alle reacties op deze\r\nbijdrage.</P>', 1039737802);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (838,3,'WebGUI','Mappen worden gebruikt om naburigen te organiseren, ongeveer op dezelfde manier waarop bestanden op de harddisk worden opgeslagen of mappen in een hangmappenkast. <p></p> <p><b>Zet in map</b><br> Mappen kunnen in andere mappen geplaatst worden. In welke map wilt u deze map plaatsen?</p> <p><b>Naam</b><br> Geef deze map een naam zodat u kunt herkennen wat er in zit.&nbsp;</p> <p><b>Beschrijving</b><br> Geef een beschrijving van de inhoud van deze map.</p> ', 1039710269);
