insert into international (internationalId,languageId,namespace,message,lastUpdated) values (75,3,'USS','De volgende sjabloon variabelen zijn beschikbaar om uw gebruikers bijdrage\r\nsysteem aan te passen.<br>\r\n<p><b>readmore.label</b><br>\r\nHet vertaalde label dat aangeeft dat de gebruiker hier moet klikken om meer\r\nte lezen. </p>\r\n<p>  <b>responses.label</b><br>\r\nHet vertaalde label dat aangeeft dat de gebruiker hier moet klikken om antwoorden\r\nop deze bijdrage te lezen. </p>\r\n<p>  <b>canPost</b><br>\r\nEen conditie die aangeeft of een gebruiker een nieuwe bijdrage mag toevoegen. \r\n</p>\r\n<p>  <b>post.url</b><br>\r\nDe URL om een nieuwe bijdrage toe te voegen. </p>\r\n<p>  <b>post.label</b><br>\r\nHet vertaalde label voor de post link. </p>\r\n<p>  <b>search.label</b><br>\r\nHet vertaalde label voor de zoek link. </p>\r\n<p>  <b>search.url</b><br>\r\nDe URL om WebGUI\'s krachtzoek formulier aan of uit te zetten. </p>\r\n<p>  <b>search.form</b><br>\r\nWebGUI\'s krachtzoek formulier. </p>\r\n<p>  <b>title.label</b><br>\r\nHet vertaalde label voor de titel kolom. </p>\r\n<p>  <b>thumbnail.label</b><br>\r\nHet vertaalde label voor de pictogram kolom. </p>\r\n<p>  <b>date.label</b><br>\r\nHet vertaalde label voor de datum kolom. </p>\r\n<p>  <b>by.label</b><br>\r\nHet vertaalde label voor dat aangeeft door wie de bijdrage bijgedragen was. \r\n</p>\r\n<p>  <b>submissions_loop</b><br>\r\nEen lus die iedere bijdrage bevat. </p>\r\n<blockquote> <b>submission.id</b><br>\r\nEen unieke waarde waaraan deze bijdrage geidentificeerd kan worden. \r\n  <p>  <b>submission.url</b><br>\r\nDe URL om deze bijdrage te bekijken. </p>\r\n  <p>  <b>submission.content</b><br>\r\nDe afgekortte tekstinhoud van deze bijdrage. </p>\r\n  <p>  <b>submission.responses</b><br>\r\nHet aantal reacties op deze bijdrage. </p>\r\n  <p>  <b>submission.title</b><br>\r\nDe titel voor deze bijdrage. </p>\r\n  <p>  <b>submission.userId</b><br>\r\nDe gebruikers Id van de gebruiker die deze bijdrage gepost heeft. </p>\r\n  <p>  <b>submission.username</b><br>\r\nDe gebruikersnaam van de gebruiker die deze bijdrage gepost heeft. </p>\r\n  <p>  <b>submission.status</b><br>\r\nDe status van deze bijdrage. (goedgekeurd,wachtend,afgekeurd). </p>\r\n  <p>  <b>submission.thumbnail</b><br>\r\nHet pictogram van het plaatje dat bij deze bijdrage is toegevoegd (als dat\r\nhet geval is). </p>\r\n  <p>  <b>submission.date</b><br>\r\nDe datum waarop deze bijdrage is gepost. </p>\r\n  <p>  <b>submission.currentUser</b><br>\r\nEen conditie die aangeeft of de huidige gebruiker dezelfde is als de gebruiker\r\ndie deze bijdrage heeft gepost. </p>\r\n  <p>  <b>submission.userProfile</b><br>\r\nDe URL naar het profiel van de gebruiker die deze bijdrage gepost heeft. </p>\r\n  <p>  <b>submission.secondColumn</b><br>\r\nEen conditie die aangeeft of deze bijdrage al dan niet in de tweede colom\r\nbehoort, in een meerdere-kolommen layout. </p>\r\n  <p>  <b>submission.thirdColumn</b><br>\r\nEen conditie die aangeeft of deze bijdrage al dan niet in de derde colom\r\nbehoort, in een meerdere-kolommen layout.&nbsp; </p>\r\n  <p>  <b>submission.fourthColumn</b><br>\r\nEen conditie die aangeeft of deze bijdrage al dan niet in de vierde colom\r\nbehoort, in een meerdere-kolommen layout.&nbsp; </p>\r\n  <p>  <b>submission.fifthColumn</b><br>\r\nEen conditie die aangeeft of deze bijdrage al dan niet in de vijfde colom\r\nbehoort, in een meerdere-kolommen layout.&nbsp; </p>\r\n  <p>  </p>\r\n</blockquote>\r\n \r\n<p>   </p>\r\n<p>  <b>lastPage</b><br>\r\n Een link naar de laatste pagina van de paginanummering. </p>\r\n \r\n<p>  <b>nextPage</b><br>\r\n Een link naar de volgende pagina van de paginanummering. </p>\r\n \r\n<p>  <b>previousPage</b><br>\r\n Een link naar de vorige pagina van de paginanummering.&nbsp; </p>\r\n \r\n<p>  <b>pageList</b><br>\r\n Een lijst van links naar alle pagina\'s in de paginanummering.&nbsp; </p>\r\n \r\n<p>  <b>multiplePages</b><br>\r\n  Een conditionele variabele die aangeeft of er meer dan een pagina in de \r\npaginanummering is.  </p>\r\n', 1039699226);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (830,3,'WebGUI','Pagina sjablonen worden gebruikt om wobjects op een pagina te organiseren.\r\nOm dit te doen moet u paginasjabloon posities maken. U kunt een onbeperkt\r\naantal posities in uw paginasjabloon opnemen, maar u moet er altijd minstens\r\neen positioneren. De paginasjabloon positie ziet er zo uit:<br>\r\n<pre>&lt;tmpl_var page.position1&gt;<br>&lt;tmpl_var page.position2&gt;<br>&lt;tmpl_var page.position3&gt;<br>&lt;tmpl_var page.position4&gt;<br>&lt;tmpl_var page.position5&gt;<br>&lt;tmpl_var page.position6&gt;<br>&lt;tmpl_var page.position7&gt;<br>&lt;tmpl_var ...&gt;<br><br></pre>\r\nDe positie elementen kunnen oplopen tot 1 miljoen wat ruim voldoende zou\r\nmoeten zijn voor een gemiddelde site.\r\n', 1039627261);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,3,'SiteMap','De volgende sjabloon variabelen zijn beschikbaar om uw sitemap aan te passen.<br>\r\n \r\n<p>  <b>page_loop</b><br>\r\nDeze lus bevat alle pagina\'s in de sitemap. </p>\r\n  <b>page.indent</b><br>\r\nHet opsommingsteken voor deze pagina die de diepte in de paginaboom aangeeft. \r\n<p>  <b>page.url</b><br>\r\nDe URL naar de pagina. </p>\r\n<p>  <b>page.id</b><br>\r\nDe unieke waarde waaraan deze pagina in WebGUI intern gerefereerd wordt. </p>\r\n<p>  <b>page.title</b><br>\r\nDe titel van deze pagina. </p>\r\n<p>  <b>page.menutitle</b><br>\r\nDe titel van deze pagina die in de navigatie wordt getoond. </p>\r\n<p>  <b>page.synopsis</b><br>\r\nDe beschrijving van de inhoud van deze pagina (als die bestaat). </p>\r\n', 1039627075);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (74,3,'MessageBoard','De volgende sjabloon variabelen zijn beschikbaar om het berichtenbord aan\r\nte passen.<br>\r\n \r\n<p>  <b>canPost</b><br>\r\nEen conditie die aangeeft of de huidige gebruiker privileges heeft om een\r\nnieuw bericht te posten. </p>\r\n<p>   <b>post.url</b><br>\r\nDe URL om een nieuw bericht te posten. </p>\r\n<p>  <b>post.label</b><br>\r\nHet vertaalde label voor de URL om een nieuw bericht te posten. </p>\r\n<p>   <b>search.url</b><br>\r\nDe URL om in dit berichtenbord te zoeken. </p>\r\n<p>  <b>search.label</b><br>\r\nHet vertaalde label voor de zoek URL. </p>\r\n<p>  <b>subject.label</b><br>\r\nHet vertaalde label voor de onderwerp kolom. </p>\r\n<p>  <b>user.label</b><br>\r\nHet vertaalde label voor de gebruiker kolom. </p>\r\n<p>  <b>date.label</b><br>\r\nHet vertaalde label voor de datum kolom. </p>\r\n<p>  <b>views.label</b><br>\r\nHet vertaalde label voor de aantal keer bekeken kolom. </p>\r\n<p>  <b>replies.label</b><br>\r\nHet vertaalde label voor de reacties kolom. </p>\r\n<p>  <b>last.label</b><br>\r\nHet vertaalde label voor de laatste reactie kolom. </p>\r\n<p>  <b>message_loop</b><br>\r\nDe lus die de berichten bevat. </p>\r\n<blockquote>  <b>last.url</b><br>\r\n De URL voor de laatste reactie op dit bericht. \r\n  <p>  <b>last.subject</b><br>\r\nHet ondewerp van de laatste reactie op dit bericht. </p>\r\n  <p>   <b>last.username</b><br>\r\nDe gebruiker die de laatste reactie op dit bericht gepost heeft. </p>\r\n  <p>  <b>last.userProfile</b><br>\r\nDe URL naar het profiel van de gebruiker die de laatste reactie op dit bericht\r\ngepost heeft. </p>\r\n  <p>  <b>last.date</b><br>\r\nDe datum waarop de laatste reactie op dit bericht gepost is. </p>\r\n  <p>   <b>message.subject</b><br>\r\nHet onderwerp van dit bericht. </p>\r\n  <p>  <b>message.date</b><br>\r\nDe datum waarop dit bericht gepost is. </p>\r\n  <p>  <b>message.user</b><br>\r\nDe gebruiker die dit bericht gepost heeft. </p>\r\n  <p>  <b>message.views</b><br>\r\nHet aantal keer dat dit bericht bekeken is. </p>\r\n  <p>  <b>message.replies</b><br>\r\nHet aantal reacties op dit bericht. </p>\r\n  <p>  <b>message.url</b><br>\r\nDe URL om dit bericht te bekijken. </p>\r\n  <p>  <b>message.currentUser</b><br>\r\nEen conditie die aangeeft of de huidige gebruiker ook de gebruiker is die\r\ndit bericht gepost heeft. </p>\r\n  <p>  <b>message.status</b><br>\r\nDe status van dit bericht (lopend, goedgekeurd, afgekeurd). </p>\r\n  <p>  </p>\r\n</blockquote>\r\n \r\n<p>   <b>firstPage</b><br>\r\nEen link naar de eerste pagina van de paginanummering. </p>\r\n<p>  <b>lastPage</b><br>\r\nEen link naar de laatste pagina van de paginanummering. </p>\r\n<p>  <b>nextPage</b><br>\r\nEen link naar de volgende pagina van de paginanummering. </p>\r\n<p>  <b>previousPage</b><br>\r\nEen link naar de vorige pagina van de paginanummering.&nbsp; </p>\r\n<p>  <b>pageList</b><br>\r\nEen lijst van links naar alle pagina\'s in de paginanummering.&nbsp; </p>\r\n<p>  <b>multiplePages</b><br>\r\n Een conditionele variabele die aangeeft of er meer dan een pagina in de\r\npaginanummering is.  </p>\r\n<br>\r\n', 1039626840);
