insert into international (internationalId,languageId,namespace,message,lastUpdated) values (633,3,'WebGUI','Simpel gezegd zijn \'roots\' pagina\'s zonder hogerliggende pagina. De eerste en belangrijkste \'root\' in WebGUI is de \"Home\" pagina. In de meeste gevallen is een extra \'root\' nooit nodig. Voorbeelden voor een extra \'root\' zijn een verborgen pagina voor administratieve redenen of een nieuwe \'root\' om de zoekmachine op te plaatsen.', 1038483226);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (627,3,'WebGUI','Profielen worden gebruikt om informatie over een bepaalde gebruiker uit te breiden. In sommige gevallen zijn profielen belangrijk voor een site, in andere gevallen niet. Het profielensysteem is volledig uitbreidbaar.', 1038480954);
