insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,3,'SyndicatedContent','Syndicated content is inhoud van een pagina die van een andere pagina gehaald\r\nwordt met behulp van de RDF/RSS specificatie. Deze techniek wordt vaak gebruikt\r\nom de koppen van verschillende nieuwspaginas zoals  <a\r\n href=\"www.cnn.com\">CNN</a> en <a href=\"http://slashdot.org\">Slashdot</a>&nbsp;te\r\nhalen. Het kan natuurlijk ook voor andere dingen zoals sportuitslagen, informatie\r\nover de aandelenmarkt, etc. gebruikt worden.<br>\r\n<br>\r\n<b>URL naar RSS bestand</b><br>\r\nVoorzie het syndicated content bestand (RDF of RSS) bestand van het volledige\r\nURL (beginnend met http://). De syndicated content wordt elk uur gedownload\r\nvan dit URL.<br>\r\n<br>\r\nSyndicated content kan onder andere op de volgende plaatsen gevonden worden:\r\n<ul>\r\n  <li><a href=\"http://www.newsisfree.com\">http://www.newsisfree.com</a></li>\r\n  <li><a href=\"http://www.syndic8.com\">http://www.syndic8.com</a></li>\r\n  <li><a href=\"http://www.voidstar.com/node.php?id=144\">http://www.voidstar.com/node.php?id=144</a></li>\r\n  <li><a href=\"http://my.userland.com\">http://my.userland.com</a></li>\r\n  <li><a href=\"http://www.webreference.com/services/news/\">http://www.webreference.com/services/news/</a></li>\r\n  <li><a href=\"http://www.xmltree.com\">http://www.xmltree.com</a></li>\r\n  <li><a href=\"http://w.moreover.com/\">http://w.moreover.com/</a><br>\r\n  </li>\r\n</ul>\r\n', 1037970486);
