insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,2,'SiteMap','SiteMaps (Seitenübersichten) stellen eine zusätzliche Navigationshilfe dar. \r\nSie können eine traditionelle Sitemap&nbsp;verwenden, die eine hierarchische \r\nAnsicht aller Seiten anzeigt. Sie können aber andererseits Sitemaps benutzen, \r\num eine zusätzliche Navigationsebene für bestimmte Ebenen Ihrer Seite einzubauen. <BR><BR><b>Template</b><BR>Wählen \r\nSie ein Layout für die Sitemap. \r\n<P><b>Beginnen mit</b><BR>Wählen Sie die Seite aus, mit der die Sitemap beginnen \r\nsoll. \r\n<BR><BR><b>Tiefe</b><BR>Wie viel Stufen der Navigation soll die Sitemap anzeigen? \r\nWenn Sie \'0\' angeben, wird die komplette Seitenübersicht angezeigt mit all ihren \r\nStufen. \r\n<P><b>Einrückung? <br></b>Wie weit soll jede nachfolgende Stufe eingerückt werden?</P>', 1041635478);
