insert into international (internationalId,languageId,namespace,message,lastUpdated) values (617,2,'WebGUI','<p>Einstellungen sind Elemente, die es Ihnen ermöglichen WebGUI an ihre besonderen \r\nBedürfnisse anzupassen. \r\n</p>\r\n<P><b>Informationen über das Unternehmen bearbeiten</b><BR>Informationen über \r\ndas Unternehmen oder individuelle Infos über den Besitzer dieser WebGUI Installation. \r\n<P><b>Inhaltliche Einstellungen bearbeiten</b><BR>Einstellungen bezüglich des \r\nInhalts und allgemeine Einstellungen bearbeiten, z. B. Editor auswählen. \r\n<P><b>Maileinstellungen bearbeiten</b><BR>Einstellungen für Mailserver, Passwortmeldung \r\nund Header von Mails. \r\n\r\n<P><b>Sonstige Einstellungen bearbeiten</b><BR>Alles, wofür wir sonst keinen \r\nPlatz gefunden haben, wie z. B. Seitenstatistik aktivieren. \r\n<P><b>Priveligierte Einstellungen bearbeiten</b><BR>Gruppen für Styles und Templates \r\nVerwalter. \r\n<p><b>Profileinstellungen bearbeiten</b><BR>Hier können!
  Sie definieren, wie \r\ndie Benutzerprofile aussehen, welche Bereiche der Benutzer bearbeiten darf, \r\nDatumsformat etc. \r\n</p>\r\n<P><b>Authentifizierungseinstellungen bearbeiten</b><BR>Einstellungen zur Benutzerregistrierung, \r\nAktivieren des Karma, LDAP Einstellungen etc.. </P>', 1041634937);
