insert into international (internationalId,languageId,namespace,message,lastUpdated) values (43,2,'Product','Merkmale beschreiben die technischen Details Ihres Produks<br><br>\r\n<b>Bezeichnung</b><br>\r\nDer Typ des Merkmals. Zum Beispiel Höhe, Breite, Gewicht, Farbe. Sie können Merkmale, die Sie bereits für andere Produkte definiert haben, übernehmen.<br><br>\r\n<b>Merkmal</b><br>\r\nDer aktuelle Wert des Merkmals. Wenn Sie z. B. als Bezeichnung \"Farbe\" gewählt haben, können Sie hier als Wert \"blau\", \"rot\" usw. definieren.<br><br>\r\n<b>Einheiten</b><br>\r\nDie Maßeinheiten für dieses Merkmal. Wenn Sie als Beispiel \"Höhe\" für die Bezeichnung gewählt haben, können Sie hier z. B. \"Zentimeter\" angeben.<br><br>\r\n<b>Weitere Merkmale hinzufügen?</b><br>\r\nHier können Sie weitere Merkmale definieren.', 1041626097);
