insert into international (internationalId,languageId,namespace,message,lastUpdated) values (76,2,'LinkList','Nachfolgend eine Liste von Template Variablen für Links-Listen Templates. \r\n<P><b>addlink.url</b><BR>Die  URL um einen Link zur Liste hinzuzufügen. \r\n<P><b>addlink.label</b><BR>Der übersetzte Text für diesen Link. \r\n<P><b>link_loop</b><BR>Diese Schleife (Loop) enthält alle Informationen über \r\njeden Link. \r\n<BLOCKQUOTE><b>link.url</b><BR>Die URL für diesen Link. \r\n<P><b>link.name</b><BR>Der verlinkte Text. \r\n<P><b>link.controls</b><BR>Die WebGUI Verwaltungscontrols für diesen Link. \r\n<P><b>link.newwindow</b><BR>Eine Variable, um den Link in einem neuen Fenster \r\n    anzeigen zu lassen. \r\n<P><b>link.description</b><BR>Die Beschreibung dieses Links.</BLOCKQUOTE>', 1041600987);
