insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,2,'EventsCalendar','Veranstaltungskalender werden z. B. in vielen Intranets benutzt, um interne Termine zu speichern, die das gesamte Unternehmen betreffen. Aber genauso sind Veranstaltungskalender eine besonders gute Möglichkeit, um Ihre Kunden auf Ihrer Unternehmensseite oder privaten Homepage über kommende Veranstaltungen oder Werbeevents zu informieren.<br><br>\r\n<b>Haupt Template</b><br>\r\nWählen Sie ein Layout für den Veranstaltungskalender.<br><br>\r\n<b>Ereignis Template</b><br>\r\nWählen Sie ein Layout für die einzelnen Termine innerhalb des Kalenders aus.<br><br>\r\n<b>Start Monat</b><br>\r\nWählen Sie hier den Startmonat Ihres Kalenders aus. Wenn Sie \"Aktuell\" auswählen, beginnt der Kalender immer im aktuellen Monat. Wenn Sie \"Erster im Kalender\" auswählen, beginnt der Kalender immer in dem Monat, in dem der früheste Termin eingetragen ist.<br><br>\r\n<b>Ende Mona!
 t</b><br>\r\nWählen Sie hier den Endemonat Ihres Kalenders aus. Wenn Sie \"X Monate vom Beginn an anzeigen\" auswählen, werden auch nur X Monate rückwirkend angezeigt. \"Aktuell\" und \"Letzter im Kalender\" gelten analog zum zuvor beschriebenen Startmonat.<br><br>\r\n<b>Standard Monat</b><br>\r\nWählt den Monat für diesen Kalender aus, der dem Besucher angezeigt wird, wenn er die Seite, die den Kalender enthält, aufruft.<br><br>\r\n<b>Einträge pro Seite</b><br>\r\nWenn Sie eine Veranstaltungsliste als Layout ausgewählt haben (z. B. Events-List), können Sie hier angeben, wieviel Termine pro Seite angezeigt werden sollen.<br><br>\r\n<b>Als nächstes?</b><br>\r\nMöchten Sie nun nach dem Erstellen des Kalenders gleich ein neues Ereignis einfügen oder zurück zur Seite gehen?<br><br>\r\n<i>Beachten Sie bitte: Termine, die bereits stattgefunden haben, werden nicht mehr im Kalender angezeigt.</i><br>\r\n<hr>\r\n<b>Achtung:</b> Der nachfolgende Style ist speziell für den Veranstaltu!
 ngskalender<br><br>\r\n<b>.eventTitle</b><br>\r\nDer Titel eines indiv
iduellen Termins.<br>', 1041595262);
