insert into international (internationalId,languageId,namespace,message,lastUpdated) values (625,2,'WebGUI','Laden Sie einige Bilder hoch, die Sie in mehreren Bereichen der Seite benutzen \r\nm√∂chten. \r\n<P><b>Name</b><BR>Die Bezeichnung, mit der Sie dieses Bild referenzieren, um \r\nes in anderen Seiten einzuf√ºgen. \r\n<P><b>In welches Verzeichnis?</b><BR>In welches Verzeichnis m√∂chten Sie diese \r\nGrafik platzieren?<P><b>Datei</b><BR>W√§hlen Sie eine Grafik aus Ihrem lokalen \r\nVerzeichnis, die Sie zum Server hochladen m√∂chten. \r\n<P><b>Parameter</b><BR>Geben Sie hier einige  HTML &lt;img Tag Parameter an, \r\ndie Sie standardm√§ssig f√ºr diese Grafik benutzen m√∂chten. \r\n<P><I>Beispiel:</I><BR>align=&quot;right&quot;<BR>alt=&quot;Dies ist ein Bild&quot;<BR>\r\n<P><b>Thumbnail Size</b><BR>Wie gross  (in Pixels) soll das Vorschaubild (Thumbnail) \r\nsein?</P>', 1042585804);
