insert into international (internationalId,languageId,namespace,message,lastUpdated) values (623,3,'WebGUI','<a href=\"http://www.w3.org/Style/css/\">Cascading Style Sheets (CSS)</a> zijn \r\neen goede manier om het uiterlijk en gevoel van een webpagina te beheren. \r\nZe worden uitgebreid gebruikt in WebGUI.<br>\r\n <br>\r\n Als er geen ervaring is met het gebruik van CSS, heeft <a\r\n href=\"http://www.plainblack.com\">Plain Black Software</a> cursussen in XHTML \r\nen CSS. Als alternatief kan de CSS editor, genaamd <a\r\n href=\"http://www.bradsoft.com/topstyle/index.asp\">Top Style</a> gebruikt \r\nworden.<br>\r\n <br>\r\n De volgende lijst bevat de verschillende klassen die gebruikt worden het \r\nuiterlijk en gevoel van WebGUI te bepalen:<br>\r\n <br>\r\n <b>A</b><br>\r\n De links in een stijl.<br>\r\n <br>\r\n <b>BODY</b><br>\r\n De tsandaard indeling van alle paginas die de stijl gebruiken.<br>\r\n <br>\r\n <b>H1</b><br>\r\n De headers (koppen) op elke pagina.<br>\r\n <br>\r\n <b>.accountOptions</b><br>\r\n De links sie onder de login en account update formulieren verschijnen.<br>\r\n <br>\r\n <b>.adminBar</b><br>\r\n De balk die bovenaan de pagina verschijnt wanneer de beheer modus gebruikt \r\nwordt.<br>\r\n <br>\r\n <b>.inhoud</b><br>\r\n Het belangrijkste inhouds gebied in elke pagina die de stijl gebruikt<br>\r\n <br>\r\n <b>.formDescription</b><br>\r\n De labels op alle formulieren naast de formulier elementen.<br>\r\n <br>\r\n <b>.formSubtext</b><br>\r\n De labels onder sommige formulier elementen.<br>\r\n  <b><br>\r\n.highlight</b><br>\r\nDuid een highlighted object aan, zoals een bericht dat bekeken wordt binnen\r\neen lijst.<br>\r\n<br>\r\n<b>.horizontalMenu</b><br>\r\nHet horizontale menu (als het horizontale menu macro gebruikt wordt).<br>\r\n<br>\r\n<b>.pagination</b><br>\r\nDe Vorige en Volgende links op paginas die opgebroken worden in meer paginas.<br>\r\n<br>\r\n<b>.selectedMenuItem</b><br>\r\nGebruik deze klasse om de huidige pagina te highlighten in een menu macro.<br>\r\n<br>\r\n<b>.tableData</b><br>\r\nDe data rijen in dingen zoals berichtenborden en gebruikersbijdragen.<br>\r\n<br>\r\n<b>.tableHeader</b><br>\r\nDe headers van kolommen in dingen zoals berichtenborden en gebruikersbijdragen.<br>\r\n <br>\r\n<b>.tableMenu</b><br>\r\nHet menu in dingen zoals berichtenborden en gebruikersbijdragen.<br>\r\n<br>\r\n<b>.verticalMenu</b><br>\r\n Het vertikale menu (als het vertikale menu macro gebruikt wordt).<br>\r\n<br>\r\n<i><b>N.B.:</b></i> Sommige wobjecten en macros hebben hun eigen unieke stylesheet\r\nklasse, die gedocumenteerd worden in hun eigen hulp bestanden.<br>\r\n', 1043314620);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (624,3,'WebGUI','WebGUI macros worden gebruikt voor het maken van dynamische inhoud binnen een voor de rest statische inhoud. Zo kan bijvoorbeeld voor elke pagina getoond  worden welke gebruikers op deze pagina ingelogd zijn of er kan een  dynamisch  gebouwd menu of een webpad weergegeven worden.<br>\r\n<br>\r\nMacros beginnen altijd met een carat (dakje) (^) en wordt vervolgd met minstens &eacute;&eacute;n ander karakter en eindigen met een puntkomma (;). Sommige  macros kunnen uitgebreid of aangepast worden door de opmaak\r\n<i>^x</i>(\"<b>aangepaste  tekst</b>\"); te gebruiken.\r\n<br>\r\n<b>N.B.</b><br>\r\nOnderstaande macros worden apart gehouden voor systeem of wobject specifieke functies zoals het SQL Verslag wobject en de Body in de Stijl Beheerder.<b>^-;, ^0;, ^1;, ^2;, ^3;, etc</b><br>', 1043314531);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (47,3,'Product','Gerelateerde produkten zijn produkten die vergelijkbaar zijn met een produkt of die een aanvulling op een produkt.<br>\r\n<br>\r\n<b>Gerelateerde produkten</b><br>\r\nTe kiezen van de lijst met produkten die al ingevoerd zijn.<br>\r\n<br>\r\n<b>Nog een gerelateerd produkt toevoegen?</b><br>\r\nSelecteer \"Ja\" als er nog een gerelateerd produkt toegevoegd gaat worden.<br>', 1043314348);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (832,3,'WebGUI','<p>Het collaterale inhoudsbeheer systeem heeft de beschikking over enkele specifieke macro\'s.</p>\r\n<b>^File();</b><br/>\r\nDit macro maakt een snelle bestandslink. Het maakt een pictogram voor het bestand en laat de bestandsnaam zien. Daarna linkt het naar dit bestand zodat het gedownload kan worden.<p/>\r\n<p><b>^I();</b><br>\r\nDit macro haalt een plaatje uit het collaterale inhoudsbeheer systeem en voegt daaraan een HTML plaatjes tag toe zodat het mogelijk is snel een plaatje uit het collaterale systeem in uw inhoud te plaatsen.</p>\r\n<p><i>Voorbeeld:</i> ^I(\"logo\");\r\n</p>\r\n<b>^i();</b><br/>\r\nDit macro haalt de URL voor willekeurige inhoud uit het systeem.\r\n<p/>\r\n<i>Voorbeeld:</i> ^i(\"status rapportage\");\r\n<p/>\r\n\r\n<b>^Snippet();</b><br/>\r\nDit makro haalt de inhoud van het knipsel uit het systeem en plakt het in de pagina.\r\n<p/>\r\n<i>Voorbeeld:</i> ^Snippet(\"flash code\");\r\n<p/>\r\n\r\n<b>^Thumbnail();</b><br/>\r\nDit makro geeft de URL voor het pictogram van een plaatje uit het systeem.\r\n<p/>\r\n<i>Voorbeeld:</i> ^Thumbnail(\"trouwfoto\");\r\n<p/>\r\n\r\n<b>^ThumbnailLinker();</b><br/>\r\nDit macro bouwt een plaatje zoals het Thumbnail macro en maakt daar een link van naar het plaatje op volledige groote.\r\n<p/>\r\n<i>Voorbeeld:</i> ^ThumbnailLinker(\"trouwfoto\");\r\n<p/>\r\n\r\n', 1043314308);
