insert into international (internationalId,languageId,namespace,message,lastUpdated) values (1002,8,'WebGUI','Qunado cancelli un database link, tutti i report SQL che usano quel link cesseranno di funzionare. Una lista di tutti i Report interessati sarà mostrato nella schermata di conferma.\r\n<p>\r\nCome ogni operazione di cancellazione, ti viene richiesto se sei certo di voler procedere alla eliminazione. Se rispondi SI la cancellazione sarà eseguita e non ci sarà più possibilità di ripristinare i dati. Se rispondi NO tornerai alla schermata precedente.', 1056803235);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (1000,8,'WebGUI','I Database Links consentono agli amministratori di WebGUI di aggiungere i database comunemente usati per utilizzarli con gli SQL Report. Questo permette agli autori del Report SQL di non dover conoscere o inserire DNS, utente e password.\r\n<p>\r\nTieni presente che ogni Database Link che crei qui sarà disponibile a tutti gli autori di contenuti. Mentre essi non potranno vedere le informazioni di connessione al database, avranno la possibilità di eseguire ogni select, visualizzazione sul database.', 1056802924);
