insert into international (internationalId,languageId,namespace,message,lastUpdated) values (72,12,'DataForm','<P>Du kan legge sÃ¥ mange felter til ditt Data Skjema som du Ã¸nsker.</P>\r\n<P><STRONG>Label</STRONG><BR>Dette er en informativ tekst som lar brukeren vita \r\nhva et felt representerer</P>\r\n<P><STRONG>Felt Navn<BR></STRONG>Navnet mÃ¥ vÃ¦re unikt blant navnet pÃ¥ felter i \r\ndette skjemaet.<BR></P>\r\n<P><STRONG>Undertekst</STRONG><BR>En utvidelse av Label, dette er en tekst som \r\nvises under feltet og bÃ¸r inneholde informasjon om hva som bÃ¸r legges inn i \r\nfeltet.</P>\r\n<P><BR><STRONG>Status<BR></STRONG>Skjulte felt vil ikke vÃ¦re synelige for \r\nbrukeren, men vil sendes sammen med ePosten.<BR>Synlige felt vil vÃ¦re synlige, \r\nmen vil ikke kunne redigeres.<BR>Redigerbare felt kan endres av bruker.<BR>Om du \r\nvelger skjult eller synlig, sÃ¥ oppgi en standardverdi(Viktig)</P>\r\n<P><STRONG>Type<BR></STRONG>Velg skjemaelement for dette feltet. \r\n<BR><BR><STRONG>Bredde<BR></STRONG>Sett antall karaktere som dette feltet skal \r\nvise i bredden</P>\r\n<P><STRONG>HÃ¸yde</STRONG><BR>Sett antall karaktere dette feltet skal vise i \r\nhÃ¸yden. Brukes bare pÃ¥ textarea og HTMLArea felttyper.</P>\r\n<P><STRONG>Mulige Verdier<BR></STRONG>Dette feltet er bare brukt i rullegardin \r\nboks typen. Bruk Mulige Verdier feltet for Ã¥ angi valgene som denne boksen skal \r\nvise. Skriv et valg pr. linje.<BR><BR><STRONG>Standard Valg \r\n(Valgfritt)<BR></STRONG>Skriv inn standardverdi (om noen) for dette feltet. For \r\nJa/Nei, Skriv Ja, for Ã¥ velge "Ja" og "Nei" for Ã¥ velge "Nei". For \r\navkryssningsboks felter skriv "Checked" for Ã¥ markere boksen.<BR><BR></P>', 1055000503);
