insert into international (internationalId,languageId,namespace,message,lastUpdated) values (834,2,'WebGUI','Sie k&ouml;nnen jede Art von Datei hochladen, um sie sp&auml;ter zu benutzen.\r\n\r\nName\r\nVergeben Sie einen eindeutigen Namen, den Sie sp&auml;ter benutzen k&ouml;nnen.\r\n\r\nIn Ordnern organisieren\r\nIn welchen Ordner soll diese Datei gespeichert werden?\r\n\r\nDatei\r\nW&auml;hlen Sie eine Datei von Ihrer Festplatte aus, die Sie hochladen m&ouml;chten.', 1056098747);
