insert into international (internationalId,languageId,namespace,message,lastUpdated) values (626,2,'WebGUI','Wobjects (fr&uuml;her auch als  Widgets bezeichnet) sind die wirkliche St&auml;rke von \r\nWebGUI. Wobjects sind kleine einbindbare Applikationen, die f&uuml;r WebGUI entwickelt \r\nwurden. Diskussionsforen und Abstimmungen sind Beispiele f&uuml;r Wobjects. \r\n<P>Um einer Seite ein Wobject hinzuzuf&uuml;gen, m&uuml;ssen sie zuerst die Seite aufrufen, \r\nin die das Wobject soll. Dann w&auml;hlen Sie ein Wobject ihrer Wahl aus dem oberen \r\nlinken Bereich (&quot;Inhalt hinzuf&uuml;gen ... &quot;) aus. Zu jedem Woject gibt \r\nes eine eigene Hilfe, daher schauen sie sich diese vorher an, wenn sie nicht \r\nwissen, welchen Zweck bzw. welche Funktionen das Wobject hat.<P><I>Style Sheets</I>: Alle Wobjects besitzen \r\neine Style-Sheet Klasse und ID. \r\n<P>Die Style-Sheet Klasse besteht aus dem Word  &quot;wobject&quot; plus dem \r\nTyp des Wobjects. Daher w&uuml;rde f&uuml;r eine Abstimmung die Style-Sheet Klasse &quot;wobjectPoll&quot; \r\nlauten. Die Klasse geh&ouml;rt dann zu allen Wobjects dieses Typs im System. \r\n<P>Die Style-Sheet ID besteht aus dem Wort &quot;wobjectId&quot; plus der Wobject Id f&uuml;r \r\ndiese Wobject Instanz. F&uuml;r einen Artikel mit der Wobject Id  94 w&uuml;rde die ID \r\ndann  &quot;wobjectId94&quot; lauten.</P>', 1048589531); 
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (623,2,'WebGUI','<A href=\"http://www.w3.org/Style/CSS/\">Cascading Style Sheets (CSS)</A> sind \r\neine gute M&ouml;glichkeit, um das &quot;Look and Feel&quot;, also das Aussehen einer \r\nSeite zu bestimmen. Sie werden sehr weitreichend in WebGUI benutzt.<P>Sollten \r\nSie mit der Benutzung von CSS nicht vertraut sein: <A href=\"http://www.plainblack.com/\">Plain Black Software</A> bietet \r\nSchulungsbereiche in XHTML und CSS an. Alternativ k&ouml;nnen Sie sich den exellenten \r\nCSS Editor von Bradsoft, <A href=\"http://www.bradsoft.com/topstyle/index.asp\">Top Style</A> \r\nanschauen.<P>Nachfolgend eine Liste von Style Klassen, die das Aussehen von \r\nWebGUI bestimmen: WebGUI: \r\n<P><B>A</B><BR>Die Links.<P><B>BODY</B><BR>Das Standard-Setup aller Seiten innerhalb \r\neines Styles. \r\n<P><B>H1</B><BR>Die &Uuml;berschriften einer jeden Seite. \r\n<P><B>.accountOptions</B><BR>DieLink, die unter den Login und Zugangsdaten \r\nFormularen angezeigt werden. \r\n<P><B>.adminBar </B><BR>Die Leiste, die im Kopf der Seite erscheint, wenn Sie \r\nim Editiermodus sind (Administrationsmen&uuml;)<P><B>.content</B><BR>Der Haupt Inhaltsbereich \r\nauf allen Seiten dieses Styles \r\n<P><B>.formDescription </B><BR>Die Zeichen der Formelemente (z. B. im Mailformular \r\nder Text &quot;Von: &quot;)<P><B>.formSubtext </B><BR>Die Zeichen unterhalb \r\nvon Form-Elementen<P><B>.highlight </B><BR>Bedeutet ein hervorgehobenes Element, \r\n so zum Beispiel k&ouml;nnen Sie hiermit den Beitrag, der gerade gelesen wird, hervorheben.<P><B>.horizontalMenu </B><BR>Das \r\nhorizontale Menu (sofern Sie eins verwenden)<P><B>.pagination </B><BR>Die &quot;Vorher&quot; \r\n/ &quot;Weiter&quot; Links auf Seiten, die sich &uuml;ber mehrere Seiten erstrecken.<P><B>.selectedMenuItem</B><BR>Hiermit \r\nk&ouml;nnen Sie in einigen der Menu-Makros die aktuelle Seite hervorheben.<P><B>.tableData </B><BR>Die \r\nDatenreihen u.a.im Diskussionsforum und im Benutzerbeitragssystem.<P><B>.tableHeader</B><BR>Die \r\nSpaltenk&ouml;pfe u.a .im Diskussionsforum und im Benutzerbeitragssystem.<P><B>.tableMenu </B><BR>Das \r\nMenu &nbsp;u.a .im Diskussionsforum und im Benutzerbeitragssystem.<P><B>.verticalMenu </B><BR>Das \r\nvertikale Men&uuml; (sofern Sie eines verwenden)<P><I><B>Beachte:</B></I> Einige \r\nWobjects und Makros haben noch ihre eigenen zus&auml;tzlichen Stylesheet Klassen, \r\ndie in den jeweiligen Hilfebereichen n&auml;her erl&auml;utert sind.</P>', 1048589344); 
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,2,'USS','Benutzerbeitragssysteme sind eine gute M&ouml;glichkeit um eine Art Gemeinschaftsgef&uuml;hl \r\nzu geben oder um kostenlose Beitr&auml;ge Ihrer Benutzer zu erhalten. <BR><BR><b>Haupt \r\nTemplate</b><BR>W&auml;hlen Sie ein Layout aus. \r\n<P><b>Inhalt filtern</b><BR>W&auml;hlen Sie die Stufe der Filterung des Contents \r\naus.. \r\n<P><b>Benutzerbeitragssystem  Template</b><BR>W&auml;hlen Sie ein Layout f&uuml;r die \r\neinzelnen Beitr&auml;ge aus. \r\n\r\n<P><b>Wer kann genehmigen?</b><BR>Welche Gruppe hat die Berechtigung, Beitr&auml;ge \r\nzu erlauben oder zu verbieten? \r\n<BR><BR><b>Wer kann Beitr&auml;ge schreiben?</b><BR>Welche Gruppe hat die Berechtigung, \r\nBeitr&auml;ge verfassen zu d&uuml;rfen? <BR><BR><b>Beitr&auml;ge pro Seite</b><BR>Wieviele \r\nBeitr&auml;ge sollen pro Seite in der &Uuml;bersicht angezeigt werden? <BR><BR><b>Standard \r\nStatus</b><BR>Sollen Beitr&auml;ge standardm&auml;ssig auf &quot;Abgelehnt&quot;, &quot;Ausstehend&quot; \r\noder &quot;freigegeben&quot; gestellt werden? <BR><BR><I>Beachten Sie:</I> Wenn \r\nSie den Status auf &quot;Ausstehend&quot; setzen, dann m&uuml;ssen Sie daran denken, \r\n&ouml;fter in Ihrem Posteingang nachzuschauen, ob Beitr&auml;ge zur Ver&ouml;ffentlichung anstehen. \r\n<P><b>Karma pro Benutzerbeitrag</b><BR>Wieviel Karma soll ein Benutzer erhalten, \r\nwenn er einen Beitrag verfasst? \r\n<P><b>Vorschaubilder anzeigen?</b><BR>Sofern eine Grafik im Beitrag enthalten \r\nist, wird das Vorschaubild im Layout angezeigt. \r\n<P><b>Diskussion erlauben?</b><BR>Do you wish to attach a discussion to this user \r\nsubmission system? If you do, users will be able to comment on each submission. \r\n<P><b>Beitr&auml;ge filtern</b><BR>W&auml;hlen Sie hier aus, wie weit Benutzerbeitr&auml;ge \r\ngefiltert werden sollen. \r\n<P><b>Wer kann Diskussionsbeitr&auml;ge schreiben?</b><BR>W&auml;hlenSie die Gruppe aus, \r\ndie berechtigt ist, Diskussionsbeitr&auml;gezu verfassen. \r\n<P><b>Timeout zum Bearbeiten</b><BR>Wie lange soll der Benutzer die M&ouml;glichkeit \r\nhaben, den von ihm verfassten Beitrag zu bearbeiten, bevor diese Bearbeitungsm&ouml;glichkeit \r\nf&uuml;r ihn gesperrt wird? \r\n<P><I>Beachten:</I> Setzen Sie das Limit nicht zu hoch. Das beste an Diskussionen \r\nist ja, dass Sie eine genaue Wiedergabe dessen darstellen sollen, wer wann was \r\ngesagt hat. . \r\n<P><b>Karma pro Diskussionsbeitrag</b><BR>Wieviel Karma soll ein Benutzer erhalten, \r\nwenn er einen Beitrag zur Diskussion verfasst? \r\n \r\n<P><b>Wer kann moderieren?</b><BR>W&auml;hlen Sie eine Gruppe aus, die diese Diskussion \r\nmoderieren kann. \r\n<P><b>Moderationsart?</b><BR>Sie k&ouml;nnen hier ausw&auml;hlen, welche Moderationsart \r\nSie bevorzugen. \r\n<ul>\r\n    <li>\'Sofort\'<I> </I>bedeutet, dass der Beitrag sofort ver&ouml;ffentlicht wird, \r\n    nachdem der Benutzer ihn verfasst hat. </li>\r\n    <li>\'Freigeben\' bedeutet, dass der Moderator den Beitrag zuerst anschauen \r\n    und freigeben muss, bevor er f&uuml;r alle sichtbar wird.</li>\r\n</ul>\r\n<p>Benachrichtigungen &uuml;ber den Eingang neuer Beitr&auml;ge/Kommentare erscheinen sofort im Posteingang des Moderators. </p><b>Bearbeitungszeitpunkt zu Beitr&auml;gen hinzuf&uuml;gen?</b><BR>M&ouml;chten \r\nSie einen &quot;Zeitstempel&quot; zu jedem Beitrag hinzuf&uuml;gen? ', 1048589165); 
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,2,'SyndicatedContent','Syndicated Content. Hierbei werden Seiten in Art eines \r\nRinges (Syndicate) zusammengeschlossen, indem Sie einen zentralen Inhalt \r\n(Content) zus&auml;tzlich zu den eigenen Informationen anbieten. Dies k&ouml;nnen \r\nbeispielsweise t&auml;gliche Nachrichten einer Branche oder eines Themas sein. Diese \r\nTechnologie wird oft genutzt, um Schlagzeilen/Nachrichten von verschiedenen \r\nSeiten anzuzeigen, wie <a href=\"http://www.heise.de\">www.heise.de</a> oder <a href=\"http://www.golem.de.\">www.golem.de.</a> \r\nEs kann nat&uuml;rlich auch f&uuml;r andere Dinge genutzt werden, wie z. B. Sportnachrichten, \r\nAktienkurse, etc.<BR><BR><b>URL zur RSS \r\nDatei</b><BR>Geben Sie die genaue  URL (beginnend mit http://) zu der angebotenen \r\nRDF oder RSS Datei an. Der Syndicated Content wird st&uuml;ndlich (sofern runHourly.pl \r\nals Cron- oder AT-Job l&auml;uft) von dieser URL gedownloadet.\r\n <BR><BR>Hier einige \r\nBeispiele, wo Sie RSS oder RDF Dateien bzw. Anbieter finden k&ouml;nnen: \r\n<P></P>\r\n<UL>\r\n<LI><a href=\"http://www.heise.de/newsticker/heise.rdf\">http://www.heise.de/newsticker/heise.rdf</a>\r\n    <li><a href=\"http://www.golem.de/golem_backend.rdf\">http://www.golem.de/golem_backend.rdf</a></li>\r\n    <li><A href=\"http://www.newsisfree.com/\">http://www.newsisfree.com</A> \r\n    </li>\r\n<LI><A href=\"http://www.syndic8.com/\">http://www.syndic8.com</A> \r\n<LI><A \r\nhref=\"http://www.voidstar.com/node.php?id=144\">http://www.voidstar.com/node.php?id=144</A> \r\n\r\n<LI><A href=\"http://my.userland.com/\">http://my.userland.com</A> \r\n<LI><A \r\nhref=\"http://www.webreference.com/services/news/\">http://www.webreference.com/services/news/</A> \r\n\r\n<LI><A href=\"http://www.xmltree.com/\">http://www.xmltree.com</A> \r\n<LI><A href=\"http://w.moreover.com/\">http://w.moreover.com/</A> </LI></UL>\r\n<P><b>Template</b><BR>W&auml;hlen Sie ein Template f&uuml;r dieses Content aus. </P>', 1048588796); 
