insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,2,'SyndicatedContent','Nachfolgend die Template Variablen f&uuml;r das Syndicated Content Template:\r\n\r\n<b>channel.title</b>\r\nDer Titel dieses Contentbereichs\r\n<b>channel.description</b>\r\nEine Beschreibung dieses Channels\r\n<b>channel.link</b>\r\nEine URL, die auf die Original-Seite dieses Channels verlinkt.\r\n<b>item_loop</b>\r\nEine Schleife, die die Daten dieses Channels enth&auml;lt.\r\n<b>title</b>\r\nDer Titel des Channelbeitrags\r\n<b>description</b>\r\nDie Beschreibung dieses Beitrags\r\n<b>link</b>\r\nEine URL zum Beitrag', 1048242285);
