insert into international (internationalId,languageId,namespace,message,lastUpdated) values (618,2,'WebGUI','<B>SMTP Server</B><BR>Dies ist die Adresse ihres lokalen \r\nMailservers. Diese wird in allen Funktionen ben&ouml;tigt, die das Internet Emailsystem \r\nverwenden (wie z. B. Passwort wiederherstellen). \r\n<P>Optional k&ouml;nnen Sie auch den Pfad zu sendmail angeben, wenn ein Sendmail-Server \r\nauf der gleichen Maschine wie WebGUI l&auml;uft. Bei den meisten Linux-Servern ist \r\ndies &quot;/usr/lib/sendmail&quot;. </P><P><B>Email Fussbereich</B>\r\n<p>Dieser \r\nFusstext ist f&uuml;r Makros vorgesehen und wird jeder Email, die von WebGUI versandt \r\nwird, angeh&auml;ngt.<P><B>Bei neuem Benutzer benachrichtigen?</B><BR>Soll jemand \r\ninformiert werden, sofern sich jemand anonym registriert hat? \r\n<P><B>Gruppe, die bei neuem Benutzer benachrichtig werden soll</B><BR>Welche \r\nGruppe soll benachrichtigt werden, wenn sich ein neuer Benutzer registriert? </P>', 1045243460); 
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (617,2,'WebGUI','<p>Einstellungen sind Elemente, die es Ihnen erm&ouml;glichen WebGUI an ihre besonderen \r\nBed&uuml;rfnisse anzupassen. \r\n</p>\r\n<P><B>Informationen &uuml;ber das Unternehmen bearbeiten</B><BR>Informationen &uuml;ber \r\ndas Unternehmen oder individuelle Personen, die diese WebGUI-Installation verwaltet.<P><B>Inhaltliche Einstellungen bearbeiten</B><BR>Einstellungen bez&uuml;glich des \r\nInhalts und allgemeine Einstellungen bearbeiten, z. B. Gr&ouml;sse der Textboxen \r\ndefinieren. \r\n<P><B>Benachrichtigungsoptionen bearbeiten</B><BR>Einstellungen bez&uuml;glich Email \r\nund andere benachrichtigungs bezogenen Funktionen. \r\n\r\n<P><B>Sonstige Einstellungen bearbeiten</B><BR>Alles, wof&uuml;r wir sonst keinen \r\nPlatz gefunden haben, wie z. B. Seitenstatistik aktivieren. \r\n<p><B>Profileinstellungen bearbeiten</B><BR>Hier k&ouml;nnen\r\n  Sie definieren, wie\r\ndie Benutzerprofile aussehen, welche Bereiche der Benutzer bearbeiten darf, \r\nDatumsformat etc. \r\n</p>\r\n<P><B>Benutzereinstellungen bearbeiten</B><BR>Einstellungen bez&uuml;glich der Benutzer \r\n(bis auf die Profileinstellungen) wie Authentifizierungs Informationen und Registierungsoptionen, \r\n \r\nAktivieren des Karma, etc.. </P>', 1045243162); 
