insert into international (internationalId,languageId,namespace,message,lastUpdated) values (43,2,'Product','Merkmale beschreiben die technischen Details Ihres Produks<br><br>\r\n<b>Bezeichnung</b><br>\r\nDer Typ des Merkmals. Zum Beispiel H&ouml;he, Breite, Gewicht, Farbe. Sie k&ouml;nnen Merkmale, die Sie bereits f&uuml;r andere Produkte definiert haben, &uuml;bernehmen.<br><br>\r\n<b>Merkmal</b><br>\r\nDer aktuelle Wert des Merkmals. Wenn Sie z. B. als Bezeichnung \"Farbe\" gew&auml;hlt haben, k&ouml;nnen Sie hier als Wert \"blau\", \"rot\" usw. definieren.<br><br>\r\n<b>Einheiten</b><br>\r\nDie Maßeinheiten f&uuml;r dieses Merkmal. Wenn Sie als Beispiel \"H&ouml;he\" f&uuml;r die Bezeichnung gew&auml;hlt haben, k&ouml;nnen Sie hier z. B. \"Zentimeter\" angeben.<br><br>\r\n<b>Weitere Merkmale hinzuf&uuml;gen?</b><br>\r\nHier k&ouml;nnen Sie weitere Merkmale definieren.', 1041626097);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (826,2,'WebGUI','WebGUI hat eine m&auml;chtige Template Sprache eingebaut, um Ihnen die maximale Kontrolle \r\n&uuml;ber Ihr Layout oder Ihren Content zu geben. Nachfolgend eine &Uuml;bersicht &uuml;ber\r\n\r\ndie Sprachsyntax. \r\n<P><b>Achtung:</b> Bitte beachten Sie, dass die W&ouml;rter <I>foo</I> und <I>bar</I> nur\r\n  \r\nals Platzhalter f&uuml;r die aktuelle Variable genutzt werden. Sie sind kein\r\nTeil \r\nder Template Sprache. \r\n<P><b>Variablen</b><BR>\r\n  Variablen sind der Grundstock von Template Befehlen. \r\nSie werden  zum Positionieren von Contentbereichen eingesetzt. \r\n<P><I>Syntax:</I> &lt;tmpl_var foo&gt; oder &lt;tmpl_var name=&quot;foo&quot;&gt; \r\n<P><I>Beispiel:</I> &lt;tmpl_var name&gt; \r\n<P><b>Bedingungsvariablen</b><BR>\r\n  F&uuml;r Programmierer sind Bedinungen (Bedingungsvariablen) \r\nnichts neues, aber bei Designern k&ouml;nnen sie schon anfangsf&uuml;r Verwirrung\r\nsorgen. \r\nBedingungen sind ganz einfach nur &quot;wahr&quot; oder &quot;falsch&quot; Fragen,\r\n\r\nund wenn Sie sich das so vorstellen, d&uuml;rften Sie keine Probleme damit haben.. \r\n<P><I>Syntax:</I> &lt;tmpl_if foo&gt; &lt;tmpl_else&gt; &lt;/tmpl_if&gt; \r\n<BR><I>Syntax:</I> &lt;tmpl_unless foo&gt; &lt;tmpl_else&gt; \r\n&lt;/tmpl_unless&gt; \r\n<P><I>Beispiel:</I> &lt;tmpl_if isTrue&gt; Es ist wahr!&lt;tmpl_else&gt; Es \r\nist falsch! &lt;/tmpl_if&gt; \r\n<P><b>Schleifen (Loops)</b><BR>\r\nLoops wiederholen sich &uuml;ber eine Liste von Datenausgaben \r\nf&uuml;r jeden Durchlauf des Loops. Loops sind etwas komplizierter als das ausschliessliche\r\n\r\nBenutzen von Variablen, aber sie sind um einiges m&auml;chtiger. \r\n<P><I>Syntax:</I> &lt;tmpl_loop foo&gt; &lt;/tmpl_loop&gt; \r\n<P><I>Beispiel:</I> <BR>&lt;tmpl_loop users&gt; <BR>&nbsp; Name: &lt;tmpl_var \r\nfirst_name&gt;&lt;br/&gt;<BR>&lt;/tmpl_loop&gt; \r\n<P><b>Schleifenbedingungen (Loop Conditions)</b><BR>\r\nLoops haben eigene Spezial \r\nSchleifenvariablen. Als da w&auml;ren __FIRST__, __!\r\n ODD__, __INNER__, und __LAST__. \r\n<P><I>Beispiele:</I><BR>\r\n<PRE>   &lt;TMPL_LOOP FOO&gt;\r\n      &lt;TMPL_IF __FIRST__&gt;\r\n       Dies ist nur der erste Durchlauf.\r\n      &lt;/TMPL_IF&gt;\r\n\r\n      &lt;TMPL_IF __ODD__&gt;\r\n        Dieses Ergebnis erscheint bei jedem Durchlauf mit ungerader Zahl.\r\n      &lt;/TMPL_IF&gt;\r\n\r\n      &lt;TMPL_UNLESS __ODD__&gt;\r\n        Dieses Ergebnis erscheint bei jedem Durchlauf mit gerader Zahl.\r\n      &lt;/TMPL_UNLESS&gt;\r\n\r\n      &lt;TMPL_IF __INNER__&gt;\r\n        Dieses Ergebnis erscheint wenn der Durchlauf weder der erste noch  der letzte ist.\r\n      &lt;/TMPL_IF&gt;\r\n\r\n      &lt;TMPL_IF __LAST__&gt;\r\n        Dieses Ergebnis erscheint erst beim letzten Druchlauf.\r\n      &lt;TMPL_IF&gt;\r\n   &lt;/TMPL_LOOP&gt;\r\n</PRE>\r\n<P><b>Beachten Sie: </b>Sowohl die Template Sprache als auch die Template Variablen\r\n  \r\nsind case-insensitive, das heisst weder Gross- noch Kleinschreibung wird ber&uuml;cksichtigt.</P>\r\n', 1041622881); 
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (95,2,'EventsCalendar','<p>Die nachfolgenden Templatevariabeln sind verf&uuml;gbar, um Ihren Veranstaltungskalender \r\nanzupassen. \r\n</p>\r\n<P><b>addevent.url</b><BR>Die URL, um ein Ereignis zum Kalender hinzuzuf&uuml;gen. \r\n<P><b>addevent.label</b><BR>Der &uuml;bersetzte Text zum Hinzuf&uuml;gen eines Ereignisses \r\n(z. B. \'Neue Veranstaltung hinzuf&uuml;gen\'). \r\n<P><b>calendar.big</b><BR>Eine komplette&nbsp;Kalenderansicht. Wochentage werden \r\nkomplett ausgeschrieben (Montag, Dienstag, ...). \r\n<P><b>calendar.small</b><BR>Eine kleinere Kalenderansicht mit abgek&uuml;rzten Wochentagen \r\n(z. B. M, D, M, D ...). \r\n<P><b>calendar.firstPage</b><BR>Ein Link zur ersten Seite im Kalender. \r\n<P><b>calendar.lastPage</b><BR>Ein Link zur letzten Seite im Kalender. \r\n<P><b>calendar.nextPage</b><BR>Ein Link zur n&auml;chsten Seite im Kalender. \r\n<P><b>calendar.previousPage</b><BR>Ein Link zur vorherigen Sei!\n te im Kalender. \r\n<P><b>calendar.pageList</b><BR>Eine Liste aller Seiten des Kalenders (z. b. \r\nSeite 1, 2, 3, ....). \r\n<P><b>calendar.multiplePages</b><BR>Eine bedingte Variable, die anzeigt, ob \r\nes mehr als eine Seite im Kalender gibt.. \r\n<P><b>list_loop</b><BR>Eine Schleife (Loop), die die Listenansicht eines Kalenders \r\numschliesst. \r\n<BLOCKQUOTE><b>list.date</b><BR>Das Datum dieses Ereignisses. \r\n<P><b>list.title</b><BR>Der Titel dieses Ereignisses. \r\n<P><b>list.description</b><BR>Die Beschreibung dieses Ereignisses. \r\n<P><b>list.sameAsPrevious</b><BR>Eine Bedingungs-Variable, falls dieses Ereigniss \r\n    das selbe Datum wie ein anderes/das letzte&nbsp;Ereignis hat. \r\n<P><b>list.url</b><BR>Die URL zur Komplettansicht dieses Ereignisses. \r\n<P><b>list.controls</b><BR>Die WebGUI Verwaltungscontrols f&uuml;r dieses Ereigniss \r\n    (z. B. Ereingiss bearbeiten). \r\n<P></P></BLOCKQUOTE>\r\n<P><b>list.firstPage</b><BR>Ein Link zur ersten Seite des Kale!\n nders (in der \r\nListenansicht). \r\n<P><b>list.lastPage</b><BR>Ein L\nink zur letzten Seite des Kalenders (in der \r\nListenansicht). \r\n<P><b>list.nextPage</b><BR> Ein Link zur n&auml;chsten Seite des Kalenders (in der \r\nListenansicht). \r\n<P><b>list.previousPage</b><BR> Ein Link zur vorherigen Seite des Kalenders \r\n(in der Listenansicht). \r\n<P><b>list.pageList</b><BR> Ein Link zu allen Seiten des Kalenders (in der Listenansicht). \r\n<P><b>list.multiplePages</b><BR>Eine Bedingungs-Variable, falls es mehr als \r\neine Seite im Kalender gibt. </P>', 1041622576); 
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (50,2,'Product','Vorz&uuml;ge oder Vorteile sind das Ergebnis der Eigenschaften Ihres Produktes. Sie sind es, warum gerade dieses Produkt so gut ist. Wenn Sie Vorz&uuml;ge hinzuf&uuml;gen, m&uuml;ssen Sie auch einige Features (Eigenschaften) hinzuf&uuml;gen.\r\n<br><br>\r\n<b>Vorz&uuml;ge</b><br>\r\nSie k&ouml;nnen entweder einen neuen Vorzug eingeben, oder einen ausw&auml;hlen, den sie bereits eingegeben haben.\r\n<br><br>\r\n<b>Neuen Vorzug hinzuf&uuml;gen?</b><br>\r\nHier k&ouml;nnen Sie weitere Vorz&uuml;ge bzw. Vorteile Ihres Produktes hinzuf&uuml;gen.', 1041622511);
