insert into international (internationalId,languageId,namespace,message,lastUpdated) values (4,2,'Survey','Umfragen erlauben Ihnen, vielf&auml;ltige Informationen von Ihren Besuchern zu erhalten. Bei den  WebGUI \r\nUmfragen k&ouml;nnen Sie sogar das Wissen der Benutzer testen, indem Sie ein Quiz einrichten. \r\n<P><b>Reihenfolge der Fragen</b><BR>Die Reihenfolge, in der die Fragen gestellt \r\nwerden. \'Der Reihe nach\' (sequentiell) stellt die Fragen in der Reihenfolge \r\ndar, in der Sie sie erstellt haben. \'Zuf&auml;llig\' zeigt die Fragen in einer zuf&auml;lligen \r\nReihenfolge an. Und &quot;Antwort gesteuert&quot; zeigt die n&auml;chsten Fragen \r\nabh&auml;ngig von der Antwort des Benutzers an. \r\n<P><b>Modus</b><BR>Standardm&auml;ssig ist die Umfrage im &quot;Umfragemodus&quot;. \r\nDamit k&ouml;nnen Sie Ihren Benutzern Fragen stellen. Aber es gibt auch noch den \r\n&quot;Quizmodus&quot;. Damit k&ouml;nnen Sie z. B. einen Test/Quiz erstellen, um \r\ndas Wissen Ihres Besuchers zu erfahren.  \r\n<P><b>Wer kann!\n  an der Umfrage teilnehmen?</b><BR>Welche Benutzer d&uuml;rfen an der \r\nUmfrage teilnehmen? \r\n\r\n<P><b>Wer kann die Ergebnisliste&nbsp;anschauen?</b><BR>Wer kann die Ergebnisliste \r\nanschauen? \r\n<P><b>Als n&auml;chstes?</b><BR>M&ouml;chten Sie nun gleich eine neue Frage eingeben?.</P>', 1041613868); 
