insert into international (internationalId,languageId,namespace,message,lastUpdated) values (41,2,'Product','Eigenschaften(Features) sind Verkaufkriterien f&uuml;r ein Produkt, vielleicht auch Gr&uuml;nde daf&uuml;r, genau dieses Produkt zu kaufen.\r\n<br><br>\r\n<b>Eigenschaft</b><br>\r\nSie k&ouml;nnen eine neue Eigenschaft angeben oder die Eigenschaft eines anderen Produktes &uuml;bernehmen.\r\n<br><br>\r\n<b>Eine weitere Eigenschaft eingeben?</b><br>\r\nM&ouml;chten Sie weitere Eigenschaften definieren?', 1041612440);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (39,2,'Product','WebGUI enth&auml;lt eine Produktverwaltung, mit dem Sie Produkte und Services schnell \r\nund einfach zu Ihrer Seite hinzuf&uuml;gen k&ouml;nnen. \r\n<P><b>Template</b><BR>W&auml;hlen Sie ein Layout f&uuml;r dieses Produkt. \r\n<P><b>Preis</b><BR>Hier k&ouml;nnen Sie einen Preis f&uuml;r dieses Produkt eingeben. \r\nSie k&ouml;nnen aber auch einen&nbsp;Text angeben, wie &quot;Rufen Sie uns an, wenn \r\nSie den Preis erfahren m&ouml;chten&quot; oder lassen Sie dieses Feld einfach leer. \r\n<P><b>Produkt Nummer</b><BR>Die Produkt Nummber, SKU, ISBN oder &auml;hnliches f&uuml;r \r\ndieses Produkt. \r\n<P><b>Produkt Grafik 1</b><BR>Eine Grafik f&uuml;r dieses  Produkt. \r\n<P><b>Produkt Grafik 2</b><BR>Eine weitere Grafik f&uuml;r dieses Produkt. \r\n<P><b>Produkt Grafik 3</b><BR>Eine dritte Grafik f&uuml;r dieses Produkt. \r\n<P><b>Prospekt/Brosch&uuml;re</b><BR>Ein Prospekt oder eine Brosch&uuml;re f&uuml;r dieses Produkt. \r\n<P><b>Handbuch</b><BR>Das !\n Produkt-, Benutzer oder Servicehandbuch  f&uuml;r dieses \r\nProdukt. \r\n<P><b>Garantie</b><BR>Die Garantie f&uuml;r dieses Produkt. </P>', 1041612070); 
