insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,2,'LinkList','Link Listen sind genau das, was der Name sagt: Eine Liste von Links. Viele Seiten haben Linkbereiche - und dieses Wobject kann zum Erstellen und Verwalten dieser Linklisten eingesetzt werden.<br><br>\r\n<b>Template</b><br>\r\nW&auml;hlen Sie ein Layout f&uuml;r die Linkliste aus.<br><br>\r\n<b>Als n&auml;chstes?</b><br>\r\nM&ouml;chten Sie gleich einen neuen Link in die Liste aufnehmen? Oder zuerst zur&uuml;ck zur Seite gehen, um sie sich anzuschauen?', 1041600773);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,2,'Item','Wie Artikel sind die Textelemente das &quot;Schweizer Messer&quot; von WebGUI. Der \r\ngr&ouml;sste Teil von statischem Inhalt kann mit Hilfe des Textelements hinzugef&uuml;gt \r\nwerden. Zusammengefasst kann man sagen, dass Textelemente benutzt werden k&ouml;nnen, \r\num kleinere Textelemente (aber auch Grafikelemente) einzuf&uuml;gen. <BR><BR><b>Link URL</b><BR>Diese URL wird \r\ndem Titel dieses Elements hinzugef&uuml;gt. <BR>\r\n<p><I>Beispiel:</I> http://www.plainblack.com \r\n<BR><BR><b>Anhang</b><BR>M&ouml;chten Sie z. B. ein Word-Dokument, ein Zip-File oder \r\nirgendeine andere Datei zum Download anbieten, so k&ouml;nnen Sie diese von Ihrem \r\nlokalen Laufwerk ausw&auml;hlen und hochladen. \r\n</p>\r\n<P><b>Template</b><BR>W&auml;hlen Sie ein Layout f&uuml;r dieses Textelement. </P>\r\n', 1041600534);
