insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,2,'FileManager','<b>Dateititel</b><BR>Der Text, der f&uuml;r diese Datei angezeigt wird. Falls Sie \r\nhier nichts angeben, wird der Dateiname angezeigt. \r\n<P><b>Dateiname</b><BR>W&auml;hlen Sie hier eine Datei von Ihrem lokalen Laufwerk, \r\ndie Sie uploaden m&ouml;chten. \r\n<P><b>Alternative #1</b><BR>Eine alternative Version dieser Datei. Wenn z. B. \r\ndie Datei im  JPEG-Format ist, k&ouml;nnten Sie bei der alternativen Version z. B. \r\nTIFF oder BMP ausw&auml;hlen. \r\n<P><b>Alternative #2</b><BR>Analog zur Alternative #1. \r\n<P><b>Kurze Beschreibung</b><BR>Eine kurze Beschreibung dieser Datei. Beachten \r\nSie, dass Sie Schl&uuml;sselw&ouml;rter benutzen, damit die Benutzer besser nach dieser \r\nDatei suchen k&ouml;nnen. \r\n<P><b>Gruppe, die Download benutzen kann</b><BR>W&auml;hlen Sie die Gruppe aus, die \r\ndiese Datei downloaden kann. \r\n<P><b>Als n&auml;chstes?</b><BR>M&ouml;chten Sie sofort&nbsp;einen neuen Downloa!\n d hinzuf&uuml;gen? \r\nOder m&ouml;chten Sie zuerst zur&uuml;ck zur Seite gehen?. </P>', 1041598859); 
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,2,'FileManager','Der Download-/Dateimanager wurde entwickelt um Ihnen eine M&ouml;glichkeit zu geben, \r\nDateien und Downloads in Ihrer Seite zu verwalten. Er bietet Ihnen die M&ouml;glichkeit \r\nzu definieren, wer Dateien anschauen oder gar downloaden kann. \r\n<P><b>Template</b><BR>W&auml;hlen Sie ein Layout f&uuml;r den Downloadmanager (z. B. \'Standard/Default\' \r\noder \'Standard ohne Suchfunktion\'. \r\n<P><b>Eintr&auml;ge pro Seite</b><BR>Wie viele Dateien sollen pro Seite angezeigt \r\nwerden? \r\n<P><b>Als n&auml;chstes?</b><BR>M&ouml;chten Sie neue Downloads hinzuf&uuml;gen oder zur&uuml;ck \r\nzur Seite gehen?.</P>', 1041598396);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (77,2,'FAQ','Nachfolgend eine Liste aller Variablen f&uuml;r FAQ Templates. \r\n<P><b>addquestion.url</b><BR>Die URL um eine Frage zur FAQ hinzuzuf&uuml;gen. \r\n<P><b>addquestion.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r diesen Link. \r\n<P><b>qa_loop</b><BR>Eine Schleife (Loop), die die Variablen f&uuml;r jede einzelne \r\nFrage der FAQ enth&auml;lt. \r\n<BLOCKQUOTE><b>qa.id</b><BR>Die ID f&uuml;r diese Frage. \r\n<P><b>qa.answer</b><BR>Die Antwort f&uuml;r diese Frage. \r\n<P><b>qa.question</b><BR>Die Frage selbst. \r\n<P><b>qa.controls</b><BR>Die WebGUI Verwaltungscontrols f&uuml;r diese Frage (z. \r\n    B. \'Frage bearbeiten\').</BLOCKQUOTE>', 1041597921);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (97,2,'EventsCalendar','Nachfolgend eine Liste von Template Variablen f&uuml;r Ereignis Templates. \r\n<P><b>title</b><BR>Der Titel des Ereignisses. \r\n<P><b>start.label</b><BR>Die &nbsp;&uuml;bersetzte Bezeichnung des Startdatums. \r\n<P><b>start.date</b><BR>Das Datum, an dem das Ereignis beginnt. \r\n<P><b>end.date</b><BR>Das Datum, an dem dieses Ereignis endet. \r\n<P><b>end.label</b><BR>Die &uuml;bersetzte Bezeichung des Ende Datums. \r\n<P><b>canEdit</b><BR>Variable, falls der Benutzer des Ereignis bearbeiten kann. \r\n<P><b>edit.url</b><BR>Die URL zum Bearbeiten dieses Ereignisses. \r\n<P><b>edit.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r diese URL (z. B. \'Termin \r\nbearbeiten\'). \r\n<P><b>delete.url</b><BR>Die URL zum L&ouml;schen dieses Ereignisses. \r\n<P><b>delete.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r diese  URL. \r\n<P><b>previous.url</b><BR>Die URL, um sich das vorherige Ereignis a!\n nzeigen zu \r\nlassen. \r\n<P><b>previous.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r diese URL. \r\n<P><b>next.url</b><BR>Die URL, um sich das n&auml;chste Ereignis anzeigen zu lassen. \r\n<p><b>next.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r diese URL. \r\n</p>\r\n<P><b>description</b><BR>Die Beschreibung f&uuml;r dieses Ereignis.</P>', 1041597266); 
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,2,'EventsCalendar','Veranstaltungskalender werden z. B. in vielen Intranets benutzt, um interne Termine zu speichern, die das gesamte Unternehmen betreffen. Aber genauso sind Veranstaltungskalender eine besonders gute M&ouml;glichkeit, um Ihre Kunden auf Ihrer Unternehmensseite oder privaten Homepage &uuml;ber kommende Veranstaltungen oder Werbeevents zu informieren.<br><br>\r\n<b>Haupt Template</b><br>\r\nW&auml;hlen Sie ein Layout f&uuml;r den Veranstaltungskalender.<br><br>\r\n<b>Ereignis Template</b><br>\r\nW&auml;hlen Sie ein Layout f&uuml;r die einzelnen Termine innerhalb des Kalenders aus.<br><br>\r\n<b>Start Monat</b><br>\r\nW&auml;hlen Sie hier den Startmonat Ihres Kalenders aus. Wenn Sie \"Aktuell\" ausw&auml;hlen, beginnt der Kalender immer im aktuellen Monat. Wenn Sie \"Erster im Kalender\" ausw&auml;hlen, beginnt der Kalender immer in dem Monat, in dem der fr&uuml;heste Termin eingetragen ist.<br><br>\r\n<b>Ende Mona!\n t</b><br>\r\nW&auml;hlen Sie hier den Endemonat Ihres Kalenders aus. Wenn Sie \"X Monate vom Beginn an anzeigen\" ausw&auml;hlen, werden auch nur X Monate r&uuml;ckwirkend angezeigt. \"Aktuell\" und \"Letzter im Kalender\" gelten analog zum zuvor beschriebenen Startmonat.<br><br>\r\n<b>Standard Monat</b><br>\r\nW&auml;hlt den Monat f&uuml;r diesen Kalender aus, der dem Besucher angezeigt wird, wenn er die Seite, die den Kalender enth&auml;lt, aufruft.<br><br>\r\n<b>Eintr&auml;ge pro Seite</b><br>\r\nWenn Sie eine Veranstaltungsliste als Layout ausgew&auml;hlt haben (z. B. Events-List), k&ouml;nnen Sie hier angeben, wieviel Termine pro Seite angezeigt werden sollen.<br><br>\r\n<b>Als n&auml;chstes?</b><br>\r\nM&ouml;chten Sie nun nach dem Erstellen des Kalenders gleich ein neues Ereignis einf&uuml;gen oder zur&uuml;ck zur Seite gehen?<br><br>\r\n<i>Beachten Sie bitte: Termine, die bereits stattgefunden haben, werden nichtmehr im Kalender angezeigt.</i><br>\r\n<hr>\r\n<b>Achtung:</b> Der nachfolgende Style ist speziell f&uuml;r den Veranstaltu!\n ngskalender<br><br>\r\n<b>.eventTitle</b><br>\r\nDer Titel eines indiv\niduellen Termins.<br>', 1041595262);
