insert into international (internationalId,languageId,namespace,message,lastUpdated,context) values (71,1,'MessageBoard','Message boards, also called Forums and/or Discussions, are a great way to add community to any site or intranet. Many companies use message boards internally to collaborate on projects.\r\n<br><br>\r\n', 1066584548,NULL);
