insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'USS','I sistemi di contribuzione da parte degli utenti sono un ottimo modo di aggiungere un senso di community su ogni sito, e anche un modo di ottenere contenuti gratuitamente dagli utenti.\r\n<br><br>\r\n<b>Template principale</b><br>\r\nselezione un layout per questo Sistema di Contributi degli Utenti<br><br>\r\n<b>Filtra il contenuto</b><br>\r\nSeleziona il livello di filtraggio del contenuto che vuoi effettuare su tutti i messaggi.<br><br>\r\n<b>Template di inserimento</b><br>\r\nselezione un layout per l\'interfaccia di sottomissione contributi<br><br>\r\n<b>Chi può approvare?</b><br>\r\nChe gruppo é abilitato ad approvare e rifiutare i contenuti?\r\n<br><br>\r\n<b>Chi può contribuire?</b><br>\r\nQuale gruppo é abilitato ad aggiungere contenuto?\r\n<br><br>\r\n<b>Contributi per pagina</b><br>\r\nQuanti contributi vuoi visualizzare per pagina nell\'indice dei contenuti?\r\n<br><br>\r\n\r\n\r\n<b>Ordina per</b><br>\r\nIl campo secondo il quale saranno ordinati i contributi. <br><br>\r\n\r\n<b>Direzione Ordinamento</b><br>\r\nLa direzione per ordinare i contributi (ascendente - discendente). <br><br>\r\n\r\n\r\n\r\n<b>Stato predefinito</b><br>\r\nI contributi vengono settati a <i>Approvato</i>, <i>Pendente</i>, o <i>Respinto</i> per default?\r\n<br><br>\r\n<i>Note:</i> Se metti lo stato predefinito a  Pendente, sii preparato a guardare spesso nel log messaggi per i nuovi contributi.\r\n\r\n<p><b>Karma Per Post</b><br>\r\nQuanto karma deve essere assegnato all\'utente quando posta&nbsp; su questa\r\ndiscussione?</p>\r\n<b>Visualizza thumbnails?</b><br>\r\nSe c\'è unna immagine nella discussione la thumbnail sarà visualizzata nel\r\nLayout .<br><br>\r\n<p><b>Consenti Discussioni?</b><br>\r\nDesideri allegare una discussione a questo sistema di contributi ? Se lo fai,\r\nogni utente potrà commentare ogni discussione.&nbsp;</p>\r\n', 1068901383);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (4,8,'Survey','Il Wobject Survey (Ricerca di Mercato) ti permette di acquisire informazioni\r\ndagli utenti del sito.\r\n<p><b>Ordine Domande</b><br>\r\nL\'ordine in cui le domande saranno poste. Sequenziale visualizza le domande\r\nnell\'ordine in cui le hai create. Random displays visualizza le domande in\r\nordine casuale. Secondo i Responsi visualizza le domande in un ordine basato\r\nsulle risposte degli utenti.\r\n<p><b>Modalità</b><br>\r\nDi default la Survey è in modalità Ricerca (survey). Questo consente di porre\r\ndomande agli utenti. L\'atra modalità è il Quiz, puoi inserire un test con\r\nauto-correzione.\r\n\r\n\r\n<p><b>Risposte anonime?</b><br>\r\nSelezione se la survey registrerà e mostrerà le informazioni che possano identificare un utente e le sue risposte.\r\nSe lasciato al valore di default "NO"  la survey registrerà l\' IP address dell\'utente come la User ID e la Username se è loggato. Queste info saranno quindi disponibili nel survey reports. Se settato su "SI", questi campi conterranno dati che non consentiranno di tracciare gli  utenti. \r\n\r\n\r\n\r\n<p><b>Chi può partecipare alla Survey?</b><br>\r\nQuali utenti possono prendere parte alla survey?\r\n<p><b>Chi può visualizzare i reports?</b><br>\r\nChi può visualizzare i risultati della survey?\r\n<p><b>Prossima azione?</b><br>\r\nSe lasci settato come visualizzato di default, potrai aggiungere una domanda\r\nsubito dopo aver aggiunto la survey.</p>\r\n', 1068900963);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'MessageBoard','I Forum di discussione sono un modo efficace di aggiungere un senso di comunità ad ogni sito internet. Molte aziende usano i Forum internamente per collaborare sui progetti.\r\n<br><br>\r\n', 1068900471);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'Article','Gli Articoli sono il coltellino svizzero del C.M.S. . La maggior parte dei contenuti statici possono essere aggiunte mediante il wobject articolo.\r\n<br><br>\r\nNOTA: Puoi creare un articolo suddiviso su più pagine mettendo la macro (^-;)separator in vari punti dell\'articolo. \r\n<br><br>\r\n<b>Template</b><br>\r\nSeleziona un&nbsp; template per il layout del tuo articolo.\r\n<br><br>\r\n<b>Immagine</b><br>\r\nScegli una immagine (.jpg, .gif, .png) dal tuo disco fisso. Questo file sarà uploadato sul server e visualizzato in alto nel tuo articolo nella posizione che scegli nella box di allineamento (sinistra-centro-destra).\r\n<br><br>\r\n<b>Titolo del link</b><br>\r\nSe vuoi aggiungere un link al tuo articolo, metti qui il titolo del link. \r\n<br><br>\r\n<i>Esempio:</i> Google\r\n<br><br>\r\n<b>URL del Link</b><br>\r\nSe ha aggiunto un titolo di link, adesso aggiungi la URL (uniform resource locator) in questo campo. \r\n<br><br>\r\n<i>Esempio:</i> http://www.google.com\r\n<br><br>\r\n<b>Allegato</b><br>\r\nSe vuoi allegare un file word, zip o qualsiasi altro file scaricabile dagli utenti, sceglilo dal tuo disco fisso.\r\n<br><br>\r\n<b>Converti gli \'a capo\'?</b><br>\r\nSe pubblichi in HTML, generalmente non c\'é bisogno di selezionare questa opzione, ma se non stai usando HTML e vuoi un a capo ogni volta che schiacci il tasto "Invio" , allora seleziona questa opzione.\r\n<p><b>Consenti&nbsp; discussioni?</b><br>\r\nSelezionando questo box abiliterai la possibilità di inviare commenti\r\nall\'articolo.\r\n<p><b>Filtra i Messaggi</b><br>\r\nSeleziona il livello di filtraggio di contenuto da eseguire sui messaggi della discussione.\r\n', 1068900404);
