insert into international (internationalId,languageId,namespace,message,lastUpdated) values (1063,8,'WebGUI','Queste sono le variabili disponibili nel template di notifica del forum.\r\n<p><b>notify.subscription.message</b><br>\r\nun messaggio che attesta che l\'utente sta ricevendo il messaggio&nbsp; perchè\r\nha sottoscritto il forum o il thread.\r\n<p><b>NOTA:</b> Il template di notifica&nbsp; include anche tutte le variabili\r\ndel post template.</p>\r\n', 1068900226);
