insert into international (internationalId,languageId,namespace,message,lastUpdated) values (72,8,'DataForm','Puoi aggiungere tutti i campi che vuoi al tuo modulo dati.\n<br>\n\n<br>\n<b>\nNome/Etichetta<br>\n</b>\nE\' un testo che dice all\'utente cosa questo campo rappresenta.</p>\n<b>\nNome Campo<br>\n</b>\nIl nome di questo campo.  Deve essere unico nell\'ambito di tutti gli altri campi del form (in pratica ogni campo deve avere un nome univoco).</p>\n<b>\nSottotitolo / Subtext<br>\n</b>\nE\' una descrizione di che cosa dovrebbe essere inserito nel campo. </p>\n<b>\n<p>Status<br>\n</b>\nI campi nascosti non saranno visibili al visitatore, ma il loro contenuto sarà spedito nel messaggio.<br>\nI campi visualizzabili possono essere visti dal visitatore, ma non modificati.<br>\nI campi modificabili possono essere compilati dal visitatore.<br>I campi obbligatori devono essere compilati.\nSe utilizzi campi nascosti o visualizzabili assicurati di aver inserito una valore di default.</p><b>\n<p>Tipo<br></b>\nScegli il tipo di elemento da assegnare a questo campo. <p>\n\n<b>larghezza<br>\n</b>\nInserisci il numero di caratteri per determinare la larghezza di questo campo.</p>\n\n<b>Altezza<br>\n</b>\nInserisci il numero di caratteri per determinare l\'altezza. Solo per textarea e htmlarea</p>\n\n<b>Valori Possibili<br>\n</b>\nQuesto campo serve solo per il Menù a tendina e i checkbox. Inserisci i valori che vuoi che vengano mostrati nel Menù a tendina, uno per linea.</p>\n<b>\n<p>Valore di Default (opzionale)<br>\n</b>\ninserisci il valore di default (se vuoi) per il campo.  Per i campi si/No, inserisci "si" per spuntare "si" e "no" per spuntare "No".\n<p>\n\n\n\n', 1054393491);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'DataForm','Questo modulo crea un form multifunzionale per inserire dati o inviare email.\n<p>\n<b>Riconoscimento</b><br>\nQuesto messaggio sarà mostrato agli utenti dopo che avranno inserito e inviato\ni dati nel modulo..\n<p><b>Componenti eMail ?</b><br>\nSe settato su si, alcuni campi addizionali dedicati alle email saranno aggiunti\nal modulo. Questi campi poi saranno utilizzati per spedire via email\nall\'indirizzo scelto, tutti i dati inseriti nel modulo.\n<p><b>NOTA:</b> Il campo &quot;A&quot; può accettare un indirizzo email\nstandard , un nome utente di WebGUI&nbsp; o un nome gruppo di WebGUI.\n<p><b>Template</b><br>\nScegli un template per il tuo modulo.\n<p><b>Email Template</b><br>\nScegli un template per i dati che saranno spediti via email.\n<p><b>Template di Riconoscimento</b><br>\nScegli un template che sarà utilizzato per mostrare il messaggio di\nriconoscimento di avvenuto inserimento.\n<p><b>Template Lista Dati</b><br>\nScegli un template che sarà utilizzato per mostrare l\'elenco dei dati\narchiviati in questo Modulo Dati.</p>\n', 1054393304);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (87,8,'DataForm','Template Lista Dati', 1054393284);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (89,8,'DataForm','Queste sono le variabili disponibili per il template List del Modulo Dati:\n<p><b>back.url</b><br>\nLa URL per tornare alla pagina di inserimento dati del Modulo.\n<p><b>back.label</b><br>\nL\'eticetta di default per il back.url.\n<p><b>field_loop</b><br>\nUn loop contenente le informazioni sui campi di questo Modulo.\n<p <blockquote><b>field.name</b><br>\nIl &quot;web safe name&quot; di questo campo.\n<p><b>field.label</b><br>\nL\'etichetta &quot;human readable&quot; per questo campo.\n<p><b>field.id</b><br>\nUn identificatore univoco che rappresenta questo campo nel database.\n<p><b>field.isMailField</b><br>\nUna condizione che indica se questo campo esiste per il &quot;sistema mail&quot;\ndel modulo dati.\n<p><b>field.type</b><br>\nIl tipo di dato associato a questo campo.\n<p>&nbsp;\n<blockquote>\n</blockquote>\n<b>record_loop</b><br>\nUn loop contenente i record inseriti in questo modulo dati.\n<p>&nbsp;\n<blockquote>\n  <b>record.entryId</b><br>\n  Un identificatore univoco per questo inserimento.\n  <p><b>record.ipAddress</b><br>\n  L\' IP Address che ha inviato questo inserimento.\n  <p><b>record.edit.url</b><br>\n  La URL per modificare questo record.\n  <p><b>record.username</b><br>\n  La username della persona che ha inviato questo inserimento.\n  <p><b>record.userId</b><br>\n  La user id della persona che ha inviato questo inserimento.\n  <p><b>record.submissionDate.epoch</b><br>\n  La epoch datestamp per questo inserimento.\n  <p><b>record.submissionDate.human</b><br>\n  La &quot;human readable date stamp&quot;, basata sulle preferenze dell\'utente,\n  per questo inserimento.\n  <p><b>record.data_loop</b><br>\n  Un loop contenente i dati inviati dall\'utente per ogni campo di questo modulo\n  dati.\n  <p>&nbsp;\n  <blockquote>\n    <b>record.data.value</b><br>\n    Il valore inviato dall\'utente per questo campo in questo inserimento.\n    <p><b>record.data.name</b><br>\n    Il &quot;web safe name&quot; di questo campo.\n    <p><b>record.data.label</b><br>\n    L\'etichetta &quot;human readable&quot; per questo campo.\n    <p><b>record.data.isMailField</b><br>\n    Una condizione che indica se questo campo esiste per il &quot;sistema\n    mail&quot; del modulo dati.\n  </blockquote>\n</blockquote>\n', 1054387767);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (83,8,'DataForm','Le seguenti sono le variabili di template disponibili per i template dei Moduli\nDati.\n<p><b>acknowledgement</b><br>\nIl Riconoscimento specificato nelle proprietà del wobject. Questo messaggio\nviene mostrato dopo che l\'utente invia i dati.\n<p><b>export.tab.url</b><br>\nSeguendo questo URL&nbsp; i dati archiviati in questo modulo dati, saranno\nesportati in un file delimitato da tab.\n<p><b>export.tab.label</b><br>\nL\'etichetta di default per la variabile export.tab.url.\n<p><b>entryList.url</b><br>\nSeguendo questo URL sarà mostrato un elenco con tutti i dati&nbsp; archiviati\nin questo modulo dati.\n<p><b>entryList.label</b><br>\nL\'etichetta di default per la variabile entryList.url .\n<p><b>canEdit</b> <br>\nUna condizione che indica se l\'utente attuale ha i privilegi per modificare un\ninserimento o per esportare i dati del modulo.\n<p><b>back.url</b><br>\nUn url che ti riporterà indietro alla pagina di default del Modulo.\n<p><b>back.label</b><br>\nL\'etichetta di default per la variabile back.url.\n<p><b>username</b>*<br>\nLa username dell\'utente che ha inviato i dati.\n<p><b>userId</b>*<br>\nLa user id dell\'utente che ha inviato i dati.\n<p><b>date</b>*<br>\nLa data in cui questi dati sono stati inseriti o aggiornati, nel formato\ndata/ora scelto dall\'utente.\n<p><b>epoch</b>*<br>\nLa data in cui questi dati sono stati inseriti o aggiornati visualizzati nel\nformato epoch della data.\n<p><b>ipAddress</b>*<br>\nL\' IP address dell\'utente che ha inviato i dati.\n<p><b>edit.url</b>*<br>\nLa URL alla pagina per modificare questo inserimento.\n<p><b>error_loop</b>*<br>\nUn loop contenente informazioni sugli errori, ad esempio se qualcuno non compila\nun campo richiesto.\n<p>&nbsp;\n<blockquote>\n  <b>error.message</b>*<br>\n  Un messaggio di errore che indica cosa l\'utente ha fatto in modo errato.\n</blockquote>\n<b>addField.url</b><br>\nLa URL che i&nbsp; content managers utilizzeranno per aggiungere nuovi campi al\nmodulo.\n<p><b>addField.label</b><br>\nL\'etichetta di default per la variabile addField.url.\n<p><b>form.start</b><br>\nL\'inizio del modulo.\n<p><b>field_loop</b><br>\nUn loop contenente tutte le informazioni sui campi.\n<p>&nbsp;\n<blockquote>\n  <b>field.form</b><br>\n  L\'elemento del form per questo campo.\n  <p><b>field.name</b><br>\n  il nome di questo campo.\n  <p><b>field.value</b><br>\n  Il &quot;value&quot; per questo campo. se è un nuovo elemento sarà usato il\n  valore di default.\n  <p><b>field.label</b><br>\n  L\'etichetta di testo per questo campo.\n  <p><b>field.isHidden</b><br>\n  Una condizione che indica se questo campo può essere nascosto.\n  <p><b>field.isDisplayed</b><br>\n  Una condizione che indica se questo campo può essere visualizzato.\n  <p><b>field.isEditable</b><br>\n  Una condizione che indica se questo campo è modificabile.\n  <p><b>field.isRequired</b><br>\n  Una condizione che indica se questo campo è obbligatorio.\n  <p><b>field.isMailField</b><br>\n  Una condizione che indica se questo campo è presente solo per inviare email.\n  <p><b>field.subtext</b><br>\n  Una descrizione del campo in modo che gli utenti capiscano cosa inserire nel\n  campo.\n  <p><b>field.controls</b><br>\n  I controlli di gestione di WebGUI per questo campo.\n  <p>&nbsp;</p>\n</blockquote>\n<b>form.send</b><br>\nUn bottone con la parola &quot;Invia&quot; .\n<p><b>form.save<br>\n</b>Un bottone con la parola &quot;Salva&quot;.<b>\n<p>form.end<br>\n</b>La fine del modulo.<b>\n<p>*Disponibili solo se l\'utente ha già inviato il modulo.</p>\n</b>', 1054386522);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (828,8,'WebGUI','Molti wobjects hanno dei templates che ti permettono di cambiare il layout\ndell\'interfaccia utente del wobject. I wobject che devono avere i template\npossiedono tutti un set di variabili di template comuni, che puoi usare per il\nlayout allo stesso modo delle rispettive variabili personalizzabili. Questa è\nuna lista delle variabili di template condivise da tutti i wobject. \n<p><b>title</b><br>\nIl titolo per questo wobject.\n<p><b>displayTitle</b><br>\nUna variabile condizionale per decidere se il titolo deve essere o no\nvisualizzato.\n<p><b>description</b><br>\nLa descrizione di questo wobject.\n<p><b>wobjectId</b><br>\nl\'identificatore univoco che&nbsp; WebGUI usa per controllare questo wobject.\n<p><b>isShortcut</b><br>\nUna varabile che indica se il wobject è un shortcut (scorciatoia) verso un wobject originale.\n<p><b>originalURL\n</b><br>\nSe questo wobject è un shortcut, allora questo URL ti dirigerà verso il wobject originale.\n\n</p>\n', 1054314520);
